import Cache from './Cache';

class CacheManager {
  private static instance: CacheManager;
  private caches: Map<string, Cache<any>>;

  private constructor() {
    this.caches = new Map();
  }

  public static getInstance(): CacheManager {
    if (!CacheManager.instance) {
      CacheManager.instance = new CacheManager();
    }
    return CacheManager.instance;
  }

  createCache<T>(name: string, defaultTTL?: number): Cache<T> {
    if (this.caches.has(name)) {
      throw new Error(`Cache with name ${name} already exists`);
    }
    if (defaultTTL !== undefined && (typeof defaultTTL !== 'number' || defaultTTL <= 0)) {
      throw new Error(`Invalid TTL value: ${defaultTTL}. TTL must be a positive number.`);
    }
    const cache = new Cache<T>(defaultTTL);
    this.caches.set(name, cache);
    this.log(`Cache created: ${name} with TTL: ${defaultTTL}`);
    return cache;
  }

  getCache<T>(name: string): Cache<T> | null {
    const cache = this.caches.get(name) as Cache<T> || null;
    if (!cache) {
      this.warn(`Cache not found: ${name}`);
    }
    return cache;
  }

  deleteCache(name: string) {
    if (this.caches.delete(name)) {
      this.log(`Cache deleted: ${name}`);
    } else {
      this.warn(`Cache delete failed: ${name} not found`);
    }
  }

  clearAllCaches() {
    this.caches.forEach(cache => cache.clear());
    this.log('All caches cleared');
  }

  getAllCacheNames(): string[] {
    return Array.from(this.caches.keys());
  }

  hasCache(name: string): boolean {
    return this.caches.has(name);
  }

  private log(message: string) {
    console.log(`[CacheManager] ${message}`);
  }

  private warn(message: string) {
    console.warn(`[CacheManager] ${message}`);
  }
}

export default CacheManager;

import CacheManager from './CacheManager';

class CacheRegistry {
  private static instance: CacheRegistry;
  private cacheManager: CacheManager;
  private services: Map<string, any>;

  private constructor() {
    this.cacheManager = CacheManager.getInstance();
    this.services = new Map();
  }

  public static getInstance(): CacheRegistry {
    if (!CacheRegistry.instance) {
      CacheRegistry.instance = new CacheRegistry();
    }
    return CacheRegistry.instance;
  }

  registerService(name: string, service: any) {
    if (this.services.has(name)) {
      throw new Error(`Service with name ${name} already registered`);
    }
    this.services.set(name, service);
    console.log(`Service registered: ${name}`);
  }

  getService<T>(name: string): T | null {
    const service = this.services.get(name) || null;
    if (!service) {
      console.warn(`Service not found: ${name}`);
    }
    return service;
  }

  getCacheManager(): CacheManager {
    return this.cacheManager;
  }
}

export default CacheRegistry;

import React from 'react';

// AI-NOTE: Error boundary for authentication and wallet errors (see artifacts)
class AuthErrorBoundary extends React.Component<{
  children: React.ReactNode
}, { hasError: boolean; error: Error | null }> {
  constructor(props: any) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error) {
    return { hasError: true, error };
  }

  componentDidCatch() {
    // No-op: error boundary logic does not use these parameters
  }

  handleRetry = () => {
    this.setState({ hasError: false, error: null });
    window.location.reload();
  };

  render() {
    if (this.state.hasError) {
      return (
        <div className="p-6 text-center">
          <h2 className="text-lg font-bold mb-2">Authentication Error</h2>
          <p className="mb-4">{this.state.error?.message || 'An unknown error occurred.'}</p>
          <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={this.handleRetry}>
            Retry
          </button>
        </div>
      );
    }
    return this.props.children;
  }
}

export default AuthErrorBoundary;

