// Alerts Data Provider for the centralized data management system
// Migrates functionality from AlertsService.ts to the new provider pattern

import { 
  DataProvider, 
  DataServiceObserver, 
  EndpointConfig, 
  FetchOptions 
} from '../interfaces';

export interface AlertData {
  id: string;
  type: 'security' | 'weather' | 'infrastructure' | 'geological' | 'space-weather';
  severity: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  description: string;
  timestamp: string;
  location?: {
    latitude: number;
    longitude: number;
    region?: string;
  };
  source: string;
  expiresAt?: string;
  affectedAreas?: string[];
}

export class AlertsDataProvider implements DataProvider<AlertData[]> {
  readonly id: string = 'alerts-data';
  readonly name: string = 'Alerts Data Provider';
  readonly endpoints: EndpointConfig[] = [
    {
      id: 'security-alerts',
      url: 'https://api.example.com/security-alerts', // Placeholder - would be real endpoint
      method: 'GET'
    },
    {
      id: 'weather-alerts',
      url: 'https://api.weather.gov/alerts/active', // Real NWS alerts API
      method: 'GET'
    },
    {
      id: 'geological-alerts',
      url: 'https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/significant_hour.geojson',
      method: 'GET'
    }
  ];

  private observer?: DataServiceObserver;

  async fetchData(key: string, _options: FetchOptions = {}): Promise<AlertData[]> {
    this.observer?.onFetchStart?.(key, this.id);
    const startTime = performance.now();

    try {
      let data: AlertData[];

      switch (key) {
        case 'security-alerts':
          data = await this.fetchSecurityAlerts();
          break;
        case 'weather-alerts':
          data = await this.fetchWeatherAlerts();
          break;
        case 'geological-alerts':
          data = await this.fetchGeologicalAlerts();
          break;
        case 'all-alerts':
          // Fetch all types and combine
          const [security, weather, geological] = await Promise.all([
            this.fetchSecurityAlerts(),
            this.fetchWeatherAlerts(),
            this.fetchGeologicalAlerts()
          ]);
          data = [...security, ...weather, ...geological];
          break;
        default:
          throw new Error(`Unknown alerts data key: ${key}`);
      }

      const duration = performance.now() - startTime;
      this.observer?.onFetchEnd?.(key, duration, this.id);

      return data;
    } catch (error) {
      const duration = performance.now() - startTime;
      this.observer?.onFetchEnd?.(key, duration, this.id);
      this.observer?.onError?.(key, error as Error, this.id);
      throw error;
    }
  }

  private async fetchSecurityAlerts(): Promise<AlertData[]> {
    // Placeholder implementation - would integrate with real security alert sources
    // For MVP, return mock data or empty array
    return [
      {
        id: 'sec-001',
        type: 'security',
        severity: 'medium',
        title: 'Cyber Security Advisory',
        description: 'Elevated threat level for infrastructure systems',
        timestamp: new Date().toISOString(),
        source: 'CISA',
        affectedAreas: ['North America']
      }
    ];
  }

  private async fetchWeatherAlerts(): Promise<AlertData[]> {
    try {
      const response = await fetch('https://api.weather.gov/alerts/active');
      if (!response.ok) {
        throw new Error(`Weather alerts API failed: ${response.status}`);
      }

      const data = await response.json();

      return data.features?.map((feature: any) => ({
        id: feature.properties.id,
        type: 'weather' as const,
        severity: this.mapNWSSeverity(feature.properties.severity),
        title: feature.properties.event,
        description: feature.properties.description || feature.properties.headline,
        timestamp: feature.properties.sent,
        location: feature.geometry ? {
          latitude: feature.geometry.coordinates[0][1],
          longitude: feature.geometry.coordinates[0][0],
          region: feature.properties.areaDesc
        } : undefined,
        source: 'National Weather Service',
        expiresAt: feature.properties.expires,
        affectedAreas: feature.properties.areaDesc ? [feature.properties.areaDesc] : []
      })) || [];
    } catch (error) {
      console.warn('Failed to fetch weather alerts:', error);
      return [];
    }
  }

  private async fetchGeologicalAlerts(): Promise<AlertData[]> {
    try {
      const response = await fetch('https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/significant_hour.geojson');
      if (!response.ok) {
        throw new Error(`USGS API failed: ${response.status}`);
      }

      const data = await response.json();

      return data.features?.map((feature: any) => ({
        id: feature.id,
        type: 'geological' as const,
        severity: this.mapEarthquakeSeverity(feature.properties.mag),
        title: `Magnitude ${feature.properties.mag} Earthquake`,
        description: feature.properties.title,
        timestamp: new Date(feature.properties.time).toISOString(),
        location: {
          latitude: feature.geometry.coordinates[1],
          longitude: feature.geometry.coordinates[0],
          region: feature.properties.place
        },
        source: 'USGS',
        affectedAreas: [feature.properties.place]
      })) || [];
    } catch (error) {
      console.warn('Failed to fetch geological alerts:', error);
      return [];
    }
  }

  subscribe(
    key: string,
    onData: (data: AlertData[]) => void,
    options: { interval?: number } = {}
  ): () => void {
    const interval = options.interval ?? 2 * 60 * 1000; // Default 2 minutes for alerts

    // Initial fetch
    this.fetchData(key)
      .then(onData)
      .catch(error => console.warn(`Initial alerts fetch failed for ${key}:`, error));

    // Set up periodic updates
    const intervalId = setInterval(async () => {
      try {
        const data = await this.fetchData(key);
        onData(data);
      } catch (error) {
        console.warn(`Alerts subscription update failed for ${key}:`, error);
      }
    }, interval);

    return () => clearInterval(intervalId);
  }

  setObserver(observer: DataServiceObserver): void {
    this.observer = observer;
  }

  validateData(data: unknown): data is AlertData[] {
    return Array.isArray(data) && data.every(item => 
      typeof item === 'object' && 
      item !== null && 
      'id' in item && 
      'type' in item && 
      'severity' in item &&
      'title' in item &&
      'timestamp' in item
    );
  }

  transformData(rawData: unknown): AlertData[] {
    if (this.validateData(rawData)) {
      return rawData;
    }
    throw new Error('Invalid data format for Alerts provider');
  }

  // Utility methods
  private mapNWSSeverity(nwsSeverity: string): AlertData['severity'] {
    switch (nwsSeverity?.toLowerCase()) {
      case 'extreme':
        return 'critical';
      case 'severe':
        return 'high';
      case 'moderate':
        return 'medium';
      case 'minor':
      default:
        return 'low';
    }
  }

  private mapEarthquakeSeverity(magnitude: number): AlertData['severity'] {
    if (magnitude >= 7.0) return 'critical';
    if (magnitude >= 6.0) return 'high';
    if (magnitude >= 5.0) return 'medium';
    return 'low';
  }

  // Static method to get all available data keys
  static getAvailableKeys(): string[] {
    return ['security-alerts', 'weather-alerts', 'geological-alerts', 'all-alerts'];
  }
}

// GeoEvents Data Provider for the centralized data management system
// Migrates functionality from GeoEventsService.ts to the new provider pattern
// Supports natural events data (earthquakes, volcanoes, etc.)

import { 
  DataProvider, 
  DataServiceObserver, 
  EndpointConfig, 
  FetchOptions 
} from '../interfaces';

// GeoEvents Data Types
export interface NaturalEvent {
  id: string | number;
  lat: number;
  lng: number;
  type: string; // e.g., 'earthquake', 'volcano', 'wildfire', 'flood'
  magnitude?: number;
  intensity?: number;
  status?: string;
  timestamp?: string;
  description?: string;
  source?: string;
}

export interface USGSEarthquakeData {
  type: 'FeatureCollection';
  features: Array<{
    type: 'Feature';
    properties: {
      mag: number;
      place: string;
      time: number;
      updated: number;
      tz: number;
      url: string;
      detail: string;
      felt: number | null;
      cdi: number | null;
      mmi: number | null;
      alert: string | null;
      status: string;
      tsunami: number;
      sig: number;
      net: string;
      code: string;
      ids: string;
      sources: string;
      types: string;
      nst: number | null;
      dmin: number | null;
      rms: number;
      gap: number | null;
      magType: string;
      type: string;
      title: string;
    };
    geometry: {
      type: 'Point';
      coordinates: [number, number, number]; // [lng, lat, depth]
    };
    id: string;
  }>;
}

export interface VolcanicEvent {
  id: string;
  name: string;
  lat: number;
  lng: number;
  elevation: number;
  type: string;
  status: 'active' | 'dormant' | 'extinct';
  last_eruption?: string;
  country: string;
  region: string;
}

// Union type for all GeoEvents data
export type GeoEventsDataTypes = 
  | NaturalEvent[] 
  | USGSEarthquakeData 
  | VolcanicEvent[];

export class GeoEventsDataProvider implements DataProvider<GeoEventsDataTypes> {
  public readonly id = 'geo-events';
  public readonly name = 'GeoEvents Data Provider';

  public readonly endpoints: EndpointConfig[] = [
    // USGS Earthquake data - real-time
    {
      id: 'earthquakes-recent',
      url: 'https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/significant_day.geojson',
      method: 'GET'
    },
    {
      id: 'earthquakes-major',
      url: 'https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/4.5_day.geojson',
      method: 'GET'
    },
    {
      id: 'earthquakes-all',
      url: 'https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_day.geojson',
      method: 'GET'
    },
    // NASA FIRMS - Fire Information for Resource Management System (wildfires)
    {
      id: 'wildfires-viirs',
      url: 'https://firms.modaps.eosdis.nasa.gov/data/active_fire/viirs/csv/VNP14IMGTDL_NRT_Global_24h.csv',
      method: 'GET'
    },
    // Legacy/fallback endpoint
    {
      id: 'natural-events-legacy',
      url: 'https://api.starcom.app/natural-events',
      method: 'GET'
    }
  ];

  private observer?: DataServiceObserver;

  async fetchData(key: string, _options: FetchOptions = {}): Promise<GeoEventsDataTypes> {
    this.observer?.onFetchStart?.(key, this.id);

    const endpoint = this.endpoints.find(e => e.id === key);
    if (!endpoint) {
      // Default to earthquakes for backward compatibility
      return this.fetchUSGSEarthquakes('earthquakes-recent');
    }

    try {
      let result: GeoEventsDataTypes;

      switch (key) {
        case 'earthquakes-recent':
        case 'earthquakes-major':
        case 'earthquakes-all':
          result = await this.fetchUSGSEarthquakes(key);
          break;
        case 'wildfires-viirs':
          result = await this.fetchWildfireData();
          break;
        case 'volcanoes':
          result = await this.fetchVolcanicData();
          break;
        case 'natural-events-legacy':
        default:
          result = await this.fetchLegacyNaturalEvents();
          break;
      }

      this.observer?.onFetchEnd?.(key, 0, this.id); // Duration tracking could be added
      return result;
    } catch (error) {
      this.observer?.onError?.(key, error as Error, this.id);
      throw error;
    }
  }

  // Migrated from original GeoEventsService.ts with improvements
  private async fetchLegacyNaturalEvents(): Promise<NaturalEvent[]> {
    const apiUrl = import.meta.env.VITE_GEO_EVENTS_API_URL || 'https://api.starcom.app/natural-events';

    const response = await fetch(apiUrl);
    if (!response.ok) {
      throw new Error(`Failed to fetch natural events: ${response.status}`);
    }

    const data = await response.json();

    // Defensive: ensure array of events
    if (Array.isArray(data)) return data;
    if (Array.isArray(data.events)) return data.events;

    return [];
  }

  // New USGS earthquake data integration
  private async fetchUSGSEarthquakes(key: string): Promise<USGSEarthquakeData> {
    const endpoint = this.endpoints.find(e => e.id === key);
    if (!endpoint) {
      throw new Error(`Earthquake endpoint not found: ${key}`);
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch USGS earthquake data: ${response.status}`);
    }

    const data = await response.json() as USGSEarthquakeData;

    // Validate USGS GeoJSON structure
    if (data.type !== 'FeatureCollection' || !Array.isArray(data.features)) {
      throw new Error('Invalid USGS earthquake data format');
    }

    return data;
  }

  // New wildfire data integration (NASA FIRMS)
  private async fetchWildfireData(): Promise<NaturalEvent[]> {
    const endpoint = this.endpoints.find(e => e.id === 'wildfires-viirs');
    if (!endpoint) {
      throw new Error('Wildfire endpoint not configured');
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch wildfire data: ${response.status}`);
    }

    const csvText = await response.text();
    const lines = csvText.split('\n');
    const headers = lines[0].split(',');

    const events: NaturalEvent[] = [];

    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',');
      if (values.length < headers.length) continue;

      const lat = parseFloat(values[0]);
      const lng = parseFloat(values[1]);
      const brightness = parseFloat(values[2]);
      const confidence = parseFloat(values[8]);

      if (!isNaN(lat) && !isNaN(lng) && confidence > 30) { // Filter low confidence detections
        events.push({
          id: `fire_${i}_${Date.now()}`,
          lat,
          lng,
          type: 'wildfire',
          intensity: brightness,
          timestamp: values[5], // scan time
          description: `Wildfire detection (confidence: ${confidence}%)`,
          source: 'NASA FIRMS VIIRS'
        });
      }
    }

    return events;
  }

  // New volcanic activity data
  private async fetchVolcanicData(): Promise<VolcanicEvent[]> {
    // Using Smithsonian's Global Volcanism Program API (if available)
    // For now, return mock data - can be expanded to real volcanic activity feeds
    return [
      {
        id: 'kilauea',
        name: 'Kilauea',
        lat: 19.421,
        lng: -155.287,
        elevation: 1247,
        type: 'Shield',
        status: 'active',
        last_eruption: '2023-09-10',
        country: 'United States',
        region: 'Hawaii'
      },
      {
        id: 'etna',
        name: 'Mount Etna',
        lat: 37.734,
        lng: 15.004,
        elevation: 3350,
        type: 'Stratovolcano',
        status: 'active',
        last_eruption: '2023-12-01',
        country: 'Italy',
        region: 'Sicily'
      }
    ];
  }

  // Transform USGS data to our unified NaturalEvent format
  transformUSGSToNaturalEvents(usgsData: USGSEarthquakeData): NaturalEvent[] {
    return usgsData.features.map(feature => ({
      id: feature.id,
      lat: feature.geometry.coordinates[1],
      lng: feature.geometry.coordinates[0],
      type: 'earthquake',
      magnitude: feature.properties.mag,
      intensity: feature.properties.sig, // Significance
      status: feature.properties.status,
      timestamp: new Date(feature.properties.time).toISOString(),
      description: feature.properties.title,
      source: 'USGS'
    }));
  }

  subscribe(
    key: string,
    onData: (data: GeoEventsDataTypes) => void,
    options: { interval?: number } = {}
  ): () => void {
    const interval = options.interval || 300000; // Default 5 minutes for geo events

    // Initial fetch
    this.fetchData(key).then(onData).catch(error => {
      this.observer?.onError?.(key, error, this.id);
    });

    // Set up polling
    const intervalId = setInterval(async () => {
      try {
        const data = await this.fetchData(key);
        onData(data);
      } catch (error) {
        this.observer?.onError?.(key, error as Error, this.id);
      }
    }, interval);

    // Return cleanup function
    return () => clearInterval(intervalId);
  }

  setObserver(observer: DataServiceObserver): void {
    this.observer = observer;
  }

  validateData(data: unknown): data is GeoEventsDataTypes {
    if (Array.isArray(data)) {
      // Check if it's an array of NaturalEvents or VolcanicEvents
      return data.every(item => 
        typeof item === 'object' && 
        item !== null &&
        'lat' in item && 
        'lng' in item &&
        typeof item.lat === 'number' &&
        typeof item.lng === 'number'
      );
    }

    // Check if it's USGS GeoJSON format
    if (typeof data === 'object' && data !== null && 'type' in data) {
      const geoData = data as USGSEarthquakeData;
      return geoData.type === 'FeatureCollection' && Array.isArray(geoData.features);
    }

    return false;
  }

  transformData(rawData: unknown): GeoEventsDataTypes {
    if (!this.validateData(rawData)) {
      throw new Error('Invalid geo events data format');
    }
    return rawData;
  }

  // Utility methods
  getEndpointConfig(key: string): EndpointConfig | undefined {
    return this.endpoints.find(e => e.id === key);
  }

  getSupportedEndpoints(): string[] {
    return this.endpoints.map(e => e.id);
  }
}

// Intel Data Provider for the centralized data management system
// Migrates functionality from IntelReportService.ts to the new provider pattern
// Supports intelligence reports from Solana blockchain and other sources

import { 
  DataProvider, 
  DataServiceObserver, 
  EndpointConfig, 
  FetchOptions 
} from '../interfaces';
import { IntelReportData } from '../../../models/IntelReportData';
import { Connection, PublicKey } from '@solana/web3.js';

// Intel Data Types
export interface IntelReport {
  pubkey: string;
  title: string;
  content: string;
  tags: string[];
  latitude: number;
  longitude: number;
  timestamp: number;
  author: string;
  signature?: string;
  verified?: boolean;
  classification?: 'UNCLASS' | 'CONFIDENTIAL' | 'SECRET' | 'TOP_SECRET';
  source?: 'SIGINT' | 'HUMINT' | 'GEOINT' | 'OSINT' | 'COMINT';
}

export interface IntelSummary {
  total_reports: number;
  recent_reports: number;
  top_tags: string[];
  geographic_coverage: {
    min_lat: number;
    max_lat: number;
    min_lng: number;
    max_lng: number;
  };
  last_updated: string;
}

export interface IntelMetrics {
  reports_by_tag: Record<string, number>;
  reports_by_author: Record<string, number>;
  reports_by_region: Record<string, number>;
  temporal_distribution: Array<{
    date: string;
    count: number;
  }>;
}

// Union type for all Intel data
export type IntelDataTypes = 
  | IntelReport[] 
  | IntelReportData[]
  | IntelSummary 
  | IntelMetrics;

export class IntelDataProvider implements DataProvider<IntelDataTypes> {
  public readonly id = 'intel-reports';
  public readonly name = 'Intelligence Data Provider';

  public readonly endpoints: EndpointConfig[] = [
    // Solana blockchain intel reports
    {
      id: 'solana-intel-reports',
      url: 'solana://program-accounts', // Special URL format for Solana
      method: 'GET'
    },
    // Intelligence summary endpoint
    {
      id: 'intel-summary',
      url: 'https://api.starcom.app/intel/summary',
      method: 'GET'
    },
    // Intelligence metrics endpoint
    {
      id: 'intel-metrics',
      url: 'https://api.starcom.app/intel/metrics',
      method: 'GET'
    },
    // OSINT aggregation endpoint
    {
      id: 'osint-feeds',
      url: 'https://api.starcom.app/intel/osint',
      method: 'GET'
    },
    // Legacy intelligence API
    {
      id: 'intel-legacy',
      url: 'https://api.starcom.app/intelligence',
      method: 'GET'
    }
  ];

  private observer?: DataServiceObserver;
  private connection?: Connection;
  private programId?: PublicKey;

  constructor(connection?: Connection, programId?: string) {
    this.connection = connection;
    if (programId) {
      try {
        this.programId = new PublicKey(programId);
      } catch (error) {
        console.warn('Invalid Solana program ID provided:', error);
      }
    }
  }

  async fetchData(key: string, _options: FetchOptions = {}): Promise<IntelDataTypes> {
    this.observer?.onFetchStart?.(key, this.id);

    try {
      let result: IntelDataTypes;

      switch (key) {
        case 'solana-intel-reports':
          result = await this.fetchSolanaIntelReports();
          break;
        case 'intel-summary':
          result = await this.fetchIntelSummary();
          break;
        case 'intel-metrics':
          result = await this.fetchIntelMetrics();
          break;
        case 'osint-feeds':
          result = await this.fetchOSINTFeeds();
          break;
        case 'intel-legacy':
        default:
          result = await this.fetchLegacyIntelReports();
          break;
      }

      this.observer?.onFetchEnd?.(key, 0, this.id);
      return result;
    } catch (error) {
      this.observer?.onError?.(key, error as Error, this.id);
      throw error;
    }
  }

  // Migrated from IntelReportService.ts
  private async fetchSolanaIntelReports(): Promise<IntelReportData[]> {
    if (!this.connection || !this.programId) {
      console.warn('Solana connection or program ID not configured, returning placeholder data');
      return this.getPlaceholderData();
    }

    try {
      console.log('Fetching intel reports from Solana program:', this.programId.toString());

      // TODO: Implement actual account fetching once program is deployed
      // const accounts = await this.connection.getProgramAccounts(this.programId);

      return this.getPlaceholderData();
    } catch (error) {
      console.error('Error fetching Solana intel reports:', error);
      // Return placeholder data for MVP development
      return this.getPlaceholderData();
    }
  }

  // New intelligence summary endpoint
  private async fetchIntelSummary(): Promise<IntelSummary> {
    const endpoint = this.endpoints.find(e => e.id === 'intel-summary');
    if (!endpoint) {
      throw new Error('Intel summary endpoint not configured');
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch intel summary: ${response.status}`);
    }

    const data = await response.json() as IntelSummary;
    return data;
  }

  // New intelligence metrics endpoint
  private async fetchIntelMetrics(): Promise<IntelMetrics> {
    const endpoint = this.endpoints.find(e => e.id === 'intel-metrics');
    if (!endpoint) {
      throw new Error('Intel metrics endpoint not configured');
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch intel metrics: ${response.status}`);
    }

    const data = await response.json() as IntelMetrics;
    return data;
  }

  // New OSINT feeds aggregation
  private async fetchOSINTFeeds(): Promise<IntelReport[]> {
    const endpoint = this.endpoints.find(e => e.id === 'osint-feeds');
    if (!endpoint) {
      throw new Error('OSINT feeds endpoint not configured');
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch OSINT feeds: ${response.status}`);
    }

    const data = await response.json();

    // Transform OSINT data to our IntelReport format
    if (Array.isArray(data)) {
      return data.map(item => this.transformToIntelReport(item));
    }

    return [];
  }

  // Legacy intel reports fallback
  private async fetchLegacyIntelReports(): Promise<IntelReport[]> {
    const endpoint = this.endpoints.find(e => e.id === 'intel-legacy');
    if (!endpoint) {
      throw new Error('Legacy intel endpoint not configured');
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch legacy intel reports: ${response.status}`);
    }

    const data = await response.json();

    if (Array.isArray(data)) {
      return data.map(item => this.transformToIntelReport(item));
    }

    return [];
  }

  // Migrated placeholder data from IntelReportService
  private getPlaceholderData(): IntelReportData[] {
    return [
      {
        pubkey: 'placeholder-1',
        title: 'Sample Intel Report - Anomalous Signal',
        content: 'Detected unusual electromagnetic patterns in sector 7G. Signal characteristics suggest artificial origin with periodic burst transmission every 47 minutes.',
        tags: ['SIGINT', 'ELECTROMAGNETIC', 'PATTERN_ANALYSIS'],
        latitude: 40.7128,
        longitude: -74.0060,
        timestamp: Date.now() - 3600000, // 1 hour ago
        author: 'Agent-Alpha-7'
      },
      {
        pubkey: 'placeholder-2', 
        title: 'Geomagnetic Disturbance Alert',
        content: 'Significant geomagnetic disturbance detected at 15:30 UTC. Potential space weather event affecting satellite communications in northern hemisphere.',
        tags: ['GEOMAGNETIC', 'SPACE_WEATHER', 'COMMS_DISRUPTION'],
        latitude: 64.2008,
        longitude: -149.4937,
        timestamp: Date.now() - 7200000, // 2 hours ago
        author: 'Station-BRAVO'
      },
      {
        pubkey: 'placeholder-3',
        title: 'Unusual Network Traffic Pattern',
        content: 'Monitoring systems detected anomalous network traffic patterns indicating possible coordinated activity. Traffic analysis shows encrypted payloads with non-standard protocols.',
        tags: ['SIGINT', 'NETWORK_ANALYSIS', 'ENCRYPTED_COMMS'],
        latitude: 51.5074,
        longitude: -0.1278,
        timestamp: Date.now() - 10800000, // 3 hours ago
        author: 'Cyber-Delta-9'
      }
    ];
  }

  // Transform various data formats to our IntelReport interface
  private transformToIntelReport(data: Record<string, unknown>): IntelReport {
    return {
      pubkey: (data.id as string) || (data.pubkey as string) || `generated-${Date.now()}`,
      title: (data.title as string) || (data.subject as string) || 'Unknown Title',
      content: (data.content as string) || (data.description as string) || (data.text as string) || '',
      tags: (data.tags as string[]) || (data.categories as string[]) || (data.keywords as string[]) || [],
      latitude: (data.latitude as number) || (data.lat as number) || ((data.location as Record<string, unknown>)?.lat as number) || 0,
      longitude: (data.longitude as number) || (data.lng as number) || ((data.location as Record<string, unknown>)?.lng as number) || 0,
      timestamp: (data.timestamp as number) || (data.created_at as number) || Date.now(),
      author: (data.author as string) || (data.source as string) || (data.collector as string) || 'Unknown',
      classification: (data.classification as 'UNCLASS' | 'CONFIDENTIAL' | 'SECRET' | 'TOP_SECRET') || 'UNCLASS',
      source: (data.intel_type as 'SIGINT' | 'HUMINT' | 'GEOINT' | 'OSINT' | 'COMINT') || (data.source_type as 'SIGINT' | 'HUMINT' | 'GEOINT' | 'OSINT' | 'COMINT') || 'OSINT',
      verified: (data.verified as boolean) || false
    };
  }

  // Filter intel reports by various criteria
  filterReports(reports: IntelReport[], filters: {
    tags?: string[];
    timeRange?: { start: number; end: number };
    geographic?: { bounds: { north: number; south: number; east: number; west: number } };
    classification?: string[];
    author?: string[];
  }): IntelReport[] {
    return reports.filter(report => {
      // Tag filtering
      if (filters.tags && filters.tags.length > 0) {
        const hasMatchingTag = filters.tags.some(tag => 
          report.tags.some(reportTag => 
            reportTag.toLowerCase().includes(tag.toLowerCase())
          )
        );
        if (!hasMatchingTag) return false;
      }

      // Time range filtering
      if (filters.timeRange) {
        if (report.timestamp < filters.timeRange.start || report.timestamp > filters.timeRange.end) {
          return false;
        }
      }

      // Geographic filtering
      if (filters.geographic) {
        const { bounds } = filters.geographic;
        if (report.latitude < bounds.south || report.latitude > bounds.north ||
            report.longitude < bounds.west || report.longitude > bounds.east) {
          return false;
        }
      }

      // Classification filtering
      if (filters.classification && filters.classification.length > 0) {
        if (!filters.classification.includes(report.classification || 'UNCLASS')) {
          return false;
        }
      }

      // Author filtering
      if (filters.author && filters.author.length > 0) {
        if (!filters.author.includes(report.author)) {
          return false;
        }
      }

      return true;
    });
  }

  subscribe(
    key: string,
    onData: (data: IntelDataTypes) => void,
    options: { interval?: number } = {}
  ): () => void {
    const interval = options.interval || 300000; // Default 5 minutes for intel reports

    // Initial fetch
    this.fetchData(key).then(onData).catch(error => {
      this.observer?.onError?.(key, error, this.id);
    });

    // Set up polling
    const intervalId = setInterval(async () => {
      try {
        const data = await this.fetchData(key);
        onData(data);
      } catch (error) {
        this.observer?.onError?.(key, error as Error, this.id);
      }
    }, interval);

    // Return cleanup function
    return () => clearInterval(intervalId);
  }

  setObserver(observer: DataServiceObserver): void {
    this.observer = observer;
  }

  validateData(data: unknown): data is IntelDataTypes {
    if (Array.isArray(data)) {
      // Check if it's an array of IntelReports or IntelReportData
      if (data.length === 0) return true; // Empty array is valid

      const firstItem = data[0];
      return (
        typeof firstItem === 'object' && 
        firstItem !== null &&
        'title' in firstItem && 
        'content' in firstItem &&
        'timestamp' in firstItem
      );
    }

    // Check if it's IntelSummary format
    if (typeof data === 'object' && data !== null && 'total_reports' in data) {
      return true;
    }

    // Check if it's IntelMetrics format
    if (typeof data === 'object' && data !== null && 'reports_by_tag' in data) {
      return true;
    }

    return false;
  }

  transformData(rawData: unknown): IntelDataTypes {
    if (!this.validateData(rawData)) {
      throw new Error('Invalid intel data format');
    }
    return rawData;
  }

  // Utility methods
  getEndpointConfig(key: string): EndpointConfig | undefined {
    return this.endpoints.find(e => e.id === key);
  }

  getSupportedEndpoints(): string[] {
    return this.endpoints.map(e => e.id);
  }

  // Configure Solana connection
  configureSolana(connection: Connection, programId: string): void {
    this.connection = connection;
    try {
      this.programId = new PublicKey(programId);
    } catch (error) {
      console.error('Invalid Solana program ID:', error);
      throw new Error('Invalid Solana program ID');
    }
  }
}

// SpaceAssets Data Provider for the centralized data management system
// Migrates functionality from SpaceAssetsService.ts to the new provider pattern
// Supports satellite tracking, space debris, and orbital asset data

import { 
  DataProvider, 
  DataServiceObserver, 
  EndpointConfig, 
  FetchOptions 
} from '../interfaces';

// SpaceAssets Data Types
export interface SpaceAsset {
  id: string | number;
  lat: number;
  lng: number;
  type: string; // e.g., 'satellite', 'debris', 'station', 'rocket'
  name: string;
  altitude: number;
  timestamp?: string;
  velocity?: number;
  inclination?: number;
  eccentricity?: number;
  period?: number; // orbital period in minutes
  classification?: 'public' | 'military' | 'commercial' | 'scientific';
  status?: 'active' | 'inactive' | 'decayed' | 'unknown';
}

export interface CelesTrakTLE {
  OBJECT_NAME: string;
  OBJECT_ID: string;
  EPOCH: string;
  MEAN_MOTION: number;
  ECCENTRICITY: number;
  INCLINATION: number;
  RA_OF_ASC_NODE: number;
  ARG_OF_PERICENTER: number;
  MEAN_ANOMALY: number;
  EPHEMERIS_TYPE: number;
  CLASSIFICATION_TYPE: string;
  NORAD_CAT_ID: number;
  ELEMENT_SET_NO: number;
  REV_AT_EPOCH: number;
  BSTAR: number;
  MEAN_MOTION_DOT: number;
  MEAN_MOTION_DDOT: number;
}

export interface N2YOSatelliteData {
  info: {
    satname: string;
    satid: number;
    transactionscount: number;
  };
  positions: Array<{
    satlatitude: number;
    satlongitude: number;
    sataltitude: number;
    azimuth: number;
    elevation: number;
    ra: number;
    dec: number;
    timestamp: number;
  }>;
}

// Union type for all SpaceAssets data
export type SpaceAssetsDataTypes = 
  | SpaceAsset[] 
  | CelesTrakTLE[] 
  | N2YOSatelliteData;

export class SpaceAssetsDataProvider implements DataProvider<SpaceAssetsDataTypes> {
  public readonly id = 'space-assets';
  public readonly name = 'Space Assets Data Provider';

  public readonly endpoints: EndpointConfig[] = [
    // CelesTrak TLE data - authoritative source for satellite tracking
    {
      id: 'active-satellites',
      url: 'https://celestrak.com/NORAD/elements/gp.php?GROUP=active&FORMAT=json',
      method: 'GET'
    },
    {
      id: 'space-stations',
      url: 'https://celestrak.com/NORAD/elements/gp.php?GROUP=stations&FORMAT=json',
      method: 'GET'
    },
    {
      id: 'starlink',
      url: 'https://celestrak.com/NORAD/elements/gp.php?GROUP=starlink&FORMAT=json',
      method: 'GET'
    },
    {
      id: 'gps-operational',
      url: 'https://celestrak.com/NORAD/elements/gp.php?GROUP=gps-ops&FORMAT=json',
      method: 'GET'
    },
    {
      id: 'debris-high-interest',
      url: 'https://celestrak.com/NORAD/elements/gp.php?GROUP=cosmos-2251-debris&FORMAT=json',
      method: 'GET'
    },
    // N2YO API for real-time satellite positions (requires API key)
    {
      id: 'iss-position',
      url: 'https://api.n2yo.com/rest/v1/satellite/positions/25544/0/0/0/1/',
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    },
    // Legacy/fallback endpoint
    {
      id: 'space-assets-legacy',
      url: 'https://api.starcom.app/space-assets',
      method: 'GET'
    }
  ];

  private observer?: DataServiceObserver;

  async fetchData(key: string, _options: FetchOptions = {}): Promise<SpaceAssetsDataTypes> {
    this.observer?.onFetchStart?.(key, this.id);

    const endpoint = this.endpoints.find(e => e.id === key);
    if (!endpoint) {
      // Default to active satellites for backward compatibility
      return this.fetchCelesTrakData('active-satellites');
    }

    try {
      let result: SpaceAssetsDataTypes;

      switch (key) {
        case 'active-satellites':
        case 'space-stations':
        case 'starlink':
        case 'gps-operational':
        case 'debris-high-interest':
          result = await this.fetchCelesTrakData(key);
          break;
        case 'iss-position':
          result = await this.fetchN2YOData(key);
          break;
        case 'space-assets-legacy':
        default:
          result = await this.fetchLegacySpaceAssets();
          break;
      }

      this.observer?.onFetchEnd?.(key, 0, this.id); // Duration tracking could be added
      return result;
    } catch (error) {
      this.observer?.onError?.(key, error as Error, this.id);
      throw error;
    }
  }

  // Migrated from original SpaceAssetsService.ts with improvements
  private async fetchLegacySpaceAssets(): Promise<SpaceAsset[]> {
    const apiUrl = import.meta.env.VITE_SPACE_ASSETS_API_URL || 'https://api.starcom.app/space-assets';

    const response = await fetch(apiUrl);
    if (!response.ok) {
      throw new Error(`Failed to fetch space assets: ${response.status}`);
    }

    const data = await response.json();

    // Defensive: ensure array of assets
    if (Array.isArray(data)) return data;
    if (Array.isArray(data.assets)) return data.assets;

    return [];
  }

  // New CelesTrak TLE data integration
  private async fetchCelesTrakData(key: string): Promise<CelesTrakTLE[]> {
    const endpoint = this.endpoints.find(e => e.id === key);
    if (!endpoint) {
      throw new Error(`CelesTrak endpoint not found: ${key}`);
    }

    const response = await fetch(endpoint.url);
    if (!response.ok) {
      throw new Error(`Failed to fetch CelesTrak data: ${response.status}`);
    }

    const data = await response.json() as CelesTrakTLE[];

    // Validate CelesTrak TLE structure
    if (!Array.isArray(data)) {
      throw new Error('Invalid CelesTrak data format: expected array');
    }

    return data;
  }

  // New N2YO API integration for real-time satellite positions
  private async fetchN2YOData(key: string): Promise<N2YOSatelliteData> {
    const endpoint = this.endpoints.find(e => e.id === key);
    if (!endpoint) {
      throw new Error(`N2YO endpoint not found: ${key}`);
    }

    const apiKey = import.meta.env.VITE_N2YO_API_KEY;
    if (!apiKey) {
      throw new Error('N2YO API key not configured');
    }

    const url = `${endpoint.url}&apiKey=${apiKey}`;
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`Failed to fetch N2YO data: ${response.status}`);
    }

    const data = await response.json() as N2YOSatelliteData;

    // Validate N2YO data structure
    if (!data.info || !Array.isArray(data.positions)) {
      throw new Error('Invalid N2YO data format');
    }

    return data;
  }

  // Transform CelesTrak TLE data to our unified SpaceAsset format
  transformCelesTrakToSpaceAssets(tleData: CelesTrakTLE[]): SpaceAsset[] {
    return tleData.map(tle => ({
      id: tle.NORAD_CAT_ID,
      name: tle.OBJECT_NAME,
      lat: 0, // Position needs orbital calculation
      lng: 0, // Position needs orbital calculation
      altitude: this.calculateAltitudeFromMeanMotion(tle.MEAN_MOTION),
      type: this.classifySpaceAsset(tle.OBJECT_NAME, tle.CLASSIFICATION_TYPE),
      timestamp: tle.EPOCH,
      inclination: tle.INCLINATION,
      eccentricity: tle.ECCENTRICITY,
      period: 1440 / tle.MEAN_MOTION, // Convert mean motion to period in minutes
      classification: tle.CLASSIFICATION_TYPE === 'U' ? 'public' : 'military',
      status: 'active' // CelesTrak typically contains active objects
    }));
  }

  // Transform N2YO data to our unified SpaceAsset format
  transformN2YOToSpaceAssets(n2yoData: N2YOSatelliteData): SpaceAsset[] {
    return n2yoData.positions.map((pos, index) => ({
      id: `${n2yoData.info.satid}_${index}`,
      name: n2yoData.info.satname,
      lat: pos.satlatitude,
      lng: pos.satlongitude,
      altitude: pos.sataltitude,
      type: 'satellite',
      timestamp: new Date(pos.timestamp * 1000).toISOString(),
      status: 'active'
    }));
  }

  // Utility method to estimate altitude from mean motion
  private calculateAltitudeFromMeanMotion(meanMotion: number): number {
    // Simplified calculation: higher mean motion = lower altitude
    // Mean motion is revolutions per day
    const period = 1440 / meanMotion; // Period in minutes
    const semiMajorAxis = Math.pow((period * 60) / (2 * Math.PI), 2) * 3.986004418e14; // GM of Earth
    const radius = Math.pow(semiMajorAxis, 1/3);
    return (radius - 6371000) / 1000; // Altitude in km above Earth's surface
  }

  // Classify space assets based on name and type
  private classifySpaceAsset(name: string, classification: string): string {
    const nameUpper = name.toUpperCase();

    if (nameUpper.includes('STARLINK')) return 'satellite';
    if (nameUpper.includes('DEBRIS') || nameUpper.includes('DEB')) return 'debris';
    if (nameUpper.includes('STATION') || nameUpper.includes('ISS')) return 'station';
    if (nameUpper.includes('ROCKET') || nameUpper.includes('R/B')) return 'rocket';
    if (nameUpper.includes('GPS') || nameUpper.includes('GALILEO') || nameUpper.includes('GLONASS')) return 'satellite';

    return classification === 'U' ? 'satellite' : 'military';
  }

  subscribe(
    key: string,
    onData: (data: SpaceAssetsDataTypes) => void,
    options: { interval?: number } = {}
  ): () => void {
    const interval = options.interval || 60000; // Default 1 minute for space assets (they move fast!)

    // Initial fetch
    this.fetchData(key).then(onData).catch(error => {
      this.observer?.onError?.(key, error, this.id);
    });

    // Set up polling
    const intervalId = setInterval(async () => {
      try {
        const data = await this.fetchData(key);
        onData(data);
      } catch (error) {
        this.observer?.onError?.(key, error as Error, this.id);
      }
    }, interval);

    // Return cleanup function
    return () => clearInterval(intervalId);
  }

  setObserver(observer: DataServiceObserver): void {
    this.observer = observer;
  }

  validateData(data: unknown): data is SpaceAssetsDataTypes {
    if (Array.isArray(data)) {
      // Check if it's an array of SpaceAssets
      if (data.length === 0) return true; // Empty array is valid

      const firstItem = data[0];
      if ('lat' in firstItem && 'lng' in firstItem && 'altitude' in firstItem) {
        // SpaceAsset format
        return data.every(item => 
          typeof item === 'object' && 
          item !== null &&
          'lat' in item && 
          'lng' in item &&
          'altitude' in item &&
          typeof item.lat === 'number' &&
          typeof item.lng === 'number' &&
          typeof item.altitude === 'number'
        );
      }

      if ('OBJECT_NAME' in firstItem && 'NORAD_CAT_ID' in firstItem) {
        // CelesTrak TLE format
        return data.every(item => 
          typeof item === 'object' && 
          item !== null &&
          'OBJECT_NAME' in item && 
          'NORAD_CAT_ID' in item
        );
      }
    }

    // Check if it's N2YO format
    if (typeof data === 'object' && data !== null && 'info' in data && 'positions' in data) {
      const n2yoData = data as N2YOSatelliteData;
      return typeof n2yoData.info === 'object' && Array.isArray(n2yoData.positions);
    }

    return false;
  }

  transformData(rawData: unknown): SpaceAssetsDataTypes {
    if (!this.validateData(rawData)) {
      throw new Error('Invalid space assets data format');
    }
    return rawData;
  }

  // Utility methods
  getEndpointConfig(key: string): EndpointConfig | undefined {
    return this.endpoints.find(e => e.id === key);
  }

  getSupportedEndpoints(): string[] {
    return this.endpoints.map(e => e.id);
  }
}

// Weather Data Provider for the centralized data management system
// Migrates functionality from WeatherDataService.ts to the new provider pattern

import { 
  DataProvider, 
  DataServiceObserver, 
  EndpointConfig, 
  FetchOptions 
} from '../interfaces';

export interface WeatherData {
  location: string;
  temperature: number;
  description: string;
  windSpeed: number;
  humidity: number;
  pressure?: number;
  visibility?: number;
  uvIndex?: number;
  coordinates: {
    latitude: number;
    longitude: number;
  };
  timestamp: string;
}

export interface WeatherForecastData {
  dateTime: string;
  temperature: number;
  description: string;
  windSpeed: number;
  humidity: number;
  precipitationChance?: number;
}

export class WeatherDataProvider implements DataProvider<WeatherData | WeatherForecastData[]> {
  readonly id: string = 'weather-data';
  readonly name: string = 'Weather Data Provider';
  readonly endpoints: EndpointConfig[] = [
    {
      id: 'current-weather',
      url: 'https://api.openweathermap.org/data/2.5/weather',
      method: 'GET',
      params: {
        units: 'metric'
      }
    },
    {
      id: 'weather-forecast',
      url: 'https://api.openweathermap.org/data/2.5/forecast',
      method: 'GET',
      params: {
        units: 'metric'
      }
    }
  ];

  private observer?: DataServiceObserver;
  private apiKey: string;

  constructor() {
    this.apiKey = import.meta.env.VITE_OPENWEATHERMAP_API_KEY || '';
    if (!this.apiKey) {
      console.warn('OpenWeatherMap API key not configured');
    }
  }

  async fetchData(key: string, _options: FetchOptions = {}): Promise<WeatherData | WeatherForecastData[]> {
    this.observer?.onFetchStart?.(key, this.id);
    const startTime = performance.now();

    try {
      let data: WeatherData | WeatherForecastData[];

      if (key.startsWith('current-weather:')) {
        const coords = this.parseCoordinates(key);
        data = await this.fetchCurrentWeather(coords.lat, coords.lng);
      } else if (key.startsWith('weather-forecast:')) {
        const coords = this.parseCoordinates(key);
        data = await this.fetchWeatherForecast(coords.lat, coords.lng);
      } else {
        throw new Error(`Unknown weather data key: ${key}`);
      }

      const duration = performance.now() - startTime;
      this.observer?.onFetchEnd?.(key, duration, this.id);

      return data;
    } catch (error) {
      const duration = performance.now() - startTime;
      this.observer?.onFetchEnd?.(key, duration, this.id);
      this.observer?.onError?.(key, error as Error, this.id);
      throw error;
    }
  }

  // Migrated from WeatherDataService.ts
  private async fetchCurrentWeather(lat: number, lng: number): Promise<WeatherData> {
    if (!this.apiKey) {
      throw new Error('OpenWeatherMap API key not configured');
    }

    const url = new URL('https://api.openweathermap.org/data/2.5/weather');
    url.searchParams.set('lat', lat.toString());
    url.searchParams.set('lon', lng.toString());
    url.searchParams.set('units', 'metric');
    url.searchParams.set('appid', this.apiKey);

    const response = await fetch(url.toString());

    if (!response.ok) {
      throw new Error(`Weather API request failed: ${response.status} ${response.statusText}`);
    }

    const rawData = await response.json();

    return {
      location: rawData.name || `${lat}, ${lng}`,
      temperature: rawData.main.temp,
      description: rawData.weather[0].description,
      windSpeed: rawData.wind?.speed || 0,
      humidity: rawData.main.humidity,
      pressure: rawData.main.pressure,
      visibility: rawData.visibility ? rawData.visibility / 1000 : undefined, // Convert to km
      uvIndex: rawData.uvi,
      coordinates: {
        latitude: lat,
        longitude: lng
      },
      timestamp: new Date().toISOString()
    };
  }

  // Migrated from WeatherDataService.ts
  private async fetchWeatherForecast(lat: number, lng: number): Promise<WeatherForecastData[]> {
    if (!this.apiKey) {
      throw new Error('OpenWeatherMap API key not configured');
    }

    const url = new URL('https://api.openweathermap.org/data/2.5/forecast');
    url.searchParams.set('lat', lat.toString());
    url.searchParams.set('lon', lng.toString());
    url.searchParams.set('units', 'metric');
    url.searchParams.set('appid', this.apiKey);

    const response = await fetch(url.toString());

    if (!response.ok) {
      throw new Error(`Weather forecast API request failed: ${response.status} ${response.statusText}`);
    }

    const rawData = await response.json();

    interface WeatherForecastEntry {
      dt_txt: string;
      main: {
        temp: number;
        humidity: number;
      };
      weather: Array<{ description: string }>;
      wind?: { speed: number };
      pop?: number; // Probability of precipitation
    }

    return rawData.list.map((entry: WeatherForecastEntry) => ({
      dateTime: entry.dt_txt,
      temperature: entry.main.temp,
      description: entry.weather[0].description,
      windSpeed: entry.wind?.speed || 0,
      humidity: entry.main.humidity,
      precipitationChance: entry.pop ? entry.pop * 100 : undefined // Convert to percentage
    }));
  }

  subscribe(
    key: string,
    onData: (data: WeatherData | WeatherForecastData[]) => void,
    options: { interval?: number } = {}
  ): () => void {
    const interval = options.interval ?? 10 * 60 * 1000; // Default 10 minutes

    // Initial fetch
    this.fetchData(key)
      .then(onData)
      .catch(error => console.warn(`Initial weather data fetch failed for ${key}:`, error));

    // Set up periodic updates
    const intervalId = setInterval(async () => {
      try {
        const data = await this.fetchData(key);
        onData(data);
      } catch (error) {
        console.warn(`Weather data subscription update failed for ${key}:`, error);
      }
    }, interval);

    return () => clearInterval(intervalId);
  }

  setObserver(observer: DataServiceObserver): void {
    this.observer = observer;
  }

  validateData(data: unknown): data is WeatherData | WeatherForecastData[] {
    if (Array.isArray(data)) {
      // Forecast data
      return data.every(item => 
        typeof item === 'object' && 
        item !== null && 
        'dateTime' in item && 
        'temperature' in item
      );
    } else {
      // Current weather data
      return typeof data === 'object' && 
             data !== null && 
             'location' in data && 
             'temperature' in data;
    }
  }

  transformData(rawData: unknown): WeatherData | WeatherForecastData[] {
    if (this.validateData(rawData)) {
      return rawData;
    }
    throw new Error('Invalid data format for Weather provider');
  }

  // Utility methods
  private parseCoordinates(key: string): { lat: number; lng: number } {
    // Expected format: "current-weather:lat,lng" or "weather-forecast:lat,lng"
    const parts = key.split(':');
    if (parts.length !== 2) {
      throw new Error(`Invalid weather key format: ${key}`);
    }

    const coords = parts[1].split(',');
    if (coords.length !== 2) {
      throw new Error(`Invalid coordinates format in key: ${key}`);
    }

    const lat = parseFloat(coords[0]);
    const lng = parseFloat(coords[1]);

    if (isNaN(lat) || isNaN(lng)) {
      throw new Error(`Invalid coordinate values in key: ${key}`);
    }

    return { lat, lng };
  }

  // Static method to create key for current weather
  static createCurrentWeatherKey(lat: number, lng: number): string {
    return `current-weather:${lat},${lng}`;
  }

  // Static method to create key for weather forecast
  static createForecastKey(lat: number, lng: number): string {
    return `weather-forecast:${lat},${lng}`;
  }

  // Static method to get all available data types
  static getAvailableKeys(): string[] {
    return ['current-weather', 'weather-forecast'];
  }
}

// Data Service Interfaces (artifact-driven)
// See: artifacts/starcom-mk2-data-service-interfaces.artifact
// See: artifacts/starcom-mk2-data-service-observability.artifact
// See: artifacts/starcom-mk2-data-service-refactor-plan.artifact

// Observability contract for logging/metrics/tracing
export interface DataServiceObserver {
  onFetchStart?(key: string): void;
  onFetchEnd?(key: string, durationMs: number): void;
  onError?(key: string, error: Error): void;
  onCacheHit?(key: string): void;
  onCacheMiss?(key: string): void;
  onCacheEvict?(key: string): void;
}

// Generic data provider interface
export interface DataProvider<T = unknown> {
  fetchData(key: string, options?: Record<string, unknown>): Promise<T>;
  subscribe?(key: string, onData: (data: T) => void, options?: Record<string, unknown>): () => void;
  setObserver?(observer: DataServiceObserver): void;
}

// Generic cache service interface
export interface DataCacheService<T = unknown> {
  get(key: string): T | null;
  set(key: string, value: T, ttl?: number): void;
  delete(key: string): void;
  clear(): void;
  has(key: string): boolean;
  setObserver?(observer: DataServiceObserver): void;
}

// Example: Composing multiple providers (fallback, aggregation)
export class FallbackProvider<T> implements DataProvider<T> {
  constructor(private providers: DataProvider<T>[]) {}
  async fetchData(key: string, options?: Record<string, unknown>): Promise<T> {
    for (const provider of this.providers) {
      try {
        return await provider.fetchData(key, options);
      } catch (e) {
        // try next provider
      }
    }
    throw new Error('All providers failed');
  }
}

