            expected: true,
            message: `Modal ${component.id} should be visible when open`
          }
        ]
      },
      {
        name: 'Form Submission Test',
        description: 'Test complete form submission flow',
        componentTypes: ['form'],
        priority: 10,
        steps: (component) => [
          {
            type: 'screenshot'
          },
