                const tagName = await input.evaluate((el: Element) => el.tagName.toLowerCase());

                if (tagName === 'input') {
                  switch (type) {
                    case 'text':
                    case 'email':
                      await input.fill('test@example.com');
                      break;
                    case 'password':
