              const menuItems = await page.locator(`${component.selector} [role="menuitem"], ${component.selector} li, ${component.selector} option`).all();
              if (menuItems.length > 0) {
                await menuItems[0].click();
              }
            }
          },
          {
            type: 'wait',
            timeout: 1000
          },
          {
