      assets,
      messages
    };
  }, [collaborationState]);

  return (
    <div className={`${styles.analytics} ${className || ''}`}>
      <div className={styles.analyticsHeader}>
        <div className={styles.analyticsTitle}>
          <span className={styles.analyticsIcon}>üìä</span>
