        </div>
      </div>

      <div className={styles.metricsGrid}>
        <MetricCard
          title="Active Sessions"
          value={`${analyticsData.activeSessions}/${analyticsData.totalSessions}`}
          trend="up"
          icon="üè¢"
        />

        <MetricCard
          title="Total Participants"
          value={analyticsData.totalParticipants}
