import React, { useState, useEffect, useRef } from 'react';
import NostrService, { NostrMessage, NostrTeamChannel } from '../../services/nostrService';
import { AgencyType, ClearanceLevel } from '../../types';
import styles from './CommunicationPanel.module.css';

interface CommunicationPanelProps {
  teamId?: string;
  userDID?: string;
  userAgency?: AgencyType;
  clearanceLevel?: ClearanceLevel;
}

const CommunicationPanel: React.FC<CommunicationPanelProps> = ({
  teamId = 'demo-team',
  userDID = 'did:socom:demo:user',
  userAgency = 'CYBER_COMMAND',
  clearanceLevel = 'CONFIDENTIAL'
}) => {
  const [messages, setMessages] = useState<NostrMessage[]>([]);
  const [activeChannel, setActiveChannel] = useState<NostrTeamChannel | null>(null);
  const [newMessage, setNewMessage] = useState('');
  const [messageType, setMessageType] = useState<NostrMessage['messageType']>('text');
  const [isConnected, setIsConnected] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const nostrService = NostrService.getInstance();

  // Initialize Nostr service and create demo channel
  useEffect(() => {
    const initializeNostr = async () => {
      try {
        // Wait for service to be ready
        if (nostrService.isReady()) {
          setIsConnected(true);
          nostrService.setUserDID(userDID);

          // Create a demo team channel
          const channel = await nostrService.createTeamChannel(
            teamId,
            `${userAgency} Secure Channel`,
            clearanceLevel,
            userAgency,
            'SOCOM/NIST compliant secure communications'
          );

          // Join the channel
          await nostrService.joinTeamChannel(channel.id, userDID, clearanceLevel);

          setActiveChannel(channel);
          setMessages(nostrService.getChannelMessages(channel.id));
        } else {
          // Retry after a short delay
          setTimeout(initializeNostr, 1000);
        }
      } catch (error) {
        console.error('Failed to initialize Nostr communication:', error);
      }
    };

    initializeNostr();
  }, [teamId, userDID, userAgency, clearanceLevel]);

  // Listen for new messages
  useEffect(() => {
    const handleMessageReceived = (event: CustomEvent) => {
      const message = event.detail as NostrMessage;
      if (activeChannel && message.channelId === activeChannel.id) {
        setMessages(prev => [...prev, message]);
        scrollToBottom();
      }
    };

    const handleMessageSent = (event: CustomEvent) => {
      const message = event.detail as NostrMessage;
      if (activeChannel && message.channelId === activeChannel.id) {
        setMessages(prev => [...prev, message]);
        scrollToBottom();
      }
    };

    window.addEventListener('nostr-message-received', handleMessageReceived as EventListener);
    window.addEventListener('nostr-message-sent', handleMessageSent as EventListener);

    return () => {
      window.removeEventListener('nostr-message-received', handleMessageReceived as EventListener);
      window.removeEventListener('nostr-message-sent', handleMessageSent as EventListener);
    };
  }, [activeChannel]);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Send message
  const handleSendMessage = async () => {
    if (!newMessage.trim() || !activeChannel || !isConnected) return;

    try {
      await nostrService.sendMessage(
        activeChannel.id,
        newMessage.trim(),
        messageType
      );
      setNewMessage('');
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  // Handle Enter key
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Get agency color
  const getAgencyColor = (agency: AgencyType): string => {
    switch (agency) {
      case 'CYBER_COMMAND': return '#00ff41';
      case 'SPACE_FORCE': return '#0099ff';
      case 'SOCOM': return '#ff6600';
      case 'NSA': return '#9900ff';
      case 'CIA': return '#ff0066';
      default: return '#ffffff';
    }
  };

  // Get clearance color
  const getClearanceColor = (level: ClearanceLevel): string => {
    switch (level) {
      case 'UNCLASSIFIED': return '#00ff00';
      case 'CONFIDENTIAL': return '#ffff00';
      case 'SECRET': return '#ff6600';
      case 'TOP_SECRET': return '#ff0000';
      default: return '#ffffff';
    }
  };

  // Format timestamp
  const formatTimestamp = (timestamp: number): string => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Simulate some demo messages for demonstration
  const simulateDemoMessages = () => {
    if (!activeChannel) return;

    const demoMessages = [
      { content: 'Threat detected in sector 7. Initiating investigation protocol.', agency: 'CYBER_COMMAND' as AgencyType, type: 'alert' as const },
      { content: 'Intelligence package uploaded to secure IPFS. Hash: QmX...abc123', agency: 'NSA' as AgencyType, type: 'intelligence' as const },
      { content: 'Team Alpha ready for deployment. All systems green.', agency: 'SOCOM' as AgencyType, type: 'status' as const }
    ];

    demoMessages.forEach((msg, index) => {
      setTimeout(() => {
        nostrService.simulateIncomingMessage(
          activeChannel.id,
          msg.content,
          msg.agency,
          msg.type
        );
      }, (index + 1) * 2000);
    });
  };

  if (!isConnected) {
    return (
      <div className={styles.communicationPanel}>
        <div className={styles.connecting}>
          <div className={styles.spinner}></div>
          <p>Establishing secure Nostr connection...</p>
        </div>
      </div>
    );
  }

  return (
    <div className={styles.communicationPanel}>
      {/* Header */}
      <div className={styles.header}>
        <div className={styles.channelInfo}>
          <h4>üîí Secure Communications</h4>
          {activeChannel && (
            <div className={styles.channelDetails}>
              <span className={styles.channelName}>{activeChannel.name}</span>
              <span 
                className={styles.clearanceBadge}
                style={{ backgroundColor: getClearanceColor(activeChannel.clearanceLevel) }}
              >
                {activeChannel.clearanceLevel}
              </span>
            </div>
          )}
        </div>
        <div className={styles.connectionStatus}>
          <div className={styles.statusIndicator}>
            <span className={styles.statusDot}></span>
            <span>Nostr Secure</span>
          </div>
          <button 
            className={styles.demoBtn}
            onClick={simulateDemoMessages}
            title="Simulate incoming messages"
          >
            üì° Demo
          </button>
        </div>
      </div>

      {/* Messages */}
      <div className={styles.messagesContainer}>
        {messages.length === 0 ? (
          <div className={styles.emptyState}>
            <p>üîê Secure channel established</p>
            <p>Quantum-safe communications ready</p>
            <button 
              className={styles.startBtn}
              onClick={simulateDemoMessages}
            >
              Start Demo Conversation
            </button>
          </div>
        ) : (
          <div className={styles.messagesList}>
            {messages.map((message) => (
              <div 
                key={message.id} 
                className={`${styles.message} ${message.senderId === nostrService.getTeamChannels()[0]?.participants[0] ? styles.sent : styles.received}`}
              >
                <div className={styles.messageHeader}>
                  <span 
                    className={styles.senderAgency}
                    style={{ color: getAgencyColor(message.senderAgency) }}
                  >
                    {message.senderAgency.replace('_', ' ')}
                  </span>
                  <span className={styles.messageType}>
                    {message.messageType === 'text' ? 'üí¨' : 
                     message.messageType === 'intelligence' ? 'üß†' :
                     message.messageType === 'alert' ? 'üö®' :
                     message.messageType === 'status' ? 'üìä' : 'üìÅ'}
                  </span>
                  <span className={styles.timestamp}>
                    {formatTimestamp(message.timestamp)}
                  </span>
                  {message.pqcEncrypted && (
                    <span className={styles.encryptionBadge} title="Post-Quantum Encrypted">
                      üîê PQC
                    </span>
                  )}
                </div>
                <div className={styles.messageContent}>
                  {message.content}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Message Input */}
      <div className={styles.messageInput}>
        <div className={styles.inputControls}>
          <select 
            value={messageType} 
            onChange={(e) => setMessageType(e.target.value as NostrMessage['messageType'])}
            className={styles.messageTypeSelect}
          >
            <option value="text">üí¨ Text</option>
            <option value="intelligence">üß† Intel</option>
            <option value="alert">üö® Alert</option>
            <option value="status">üìä Status</option>
            <option value="file">üìÅ File</option>
          </select>
        </div>
        <div className={styles.inputRow}>
          <textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={`Send secure ${messageType} message...`}
            className={styles.textInput}
            rows={2}
          />
          <button 
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
            className={styles.sendButton}
          >
            Send üîí
          </button>
        </div>
        <div className={styles.securityInfo}>
          <span>üîê End-to-end encrypted</span>
          <span>üõ°Ô∏è Quantum-safe</span>
          <span>üìù Audit logged</span>
        </div>
      </div>
    </div>
  );
};

export default CommunicationPanel;

import React, { useRef, useEffect, useCallback } from 'react';
import { useGlobalCommand } from '../../../hooks/useUnifiedGlobalCommand';
import styles from './NodeGraphView.module.css';

interface NodeGraphViewProps {
  className?: string;
  fullscreen?: boolean;
}

interface GraphNode {
  id: string;
  x: number;
  y: number;
  type: 'entity' | 'event' | 'location' | 'system' | 'threat';
  label: string;
  size: number;
  color: string;
  connections: string[];
}

interface GraphEdge {
  from: string;
  to: string;
  type: 'related' | 'causes' | 'located_at' | 'communicates';
  strength: number;
  label?: string;
}

const NodeGraphView: React.FC<NodeGraphViewProps> = ({ className, fullscreen }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const { state } = useGlobalCommand();

  const sampleNodes: GraphNode[] = [
    { id: 'entity1', x: 200, y: 150, type: 'entity', label: 'TARGET ALPHA', size: 20, color: '#ff3333', connections: ['event1', 'location1'] },
    { id: 'event1', x: 400, y: 100, type: 'event', label: 'SECURITY BREACH', size: 16, color: '#ff6600', connections: ['entity1', 'system1'] },
    { id: 'location1', x: 300, y: 250, type: 'location', label: 'DATA CENTER', size: 18, color: '#3366ff', connections: ['entity1', 'system1'] },
    { id: 'system1', x: 500, y: 200, type: 'system', label: 'NETWORK NODE', size: 14, color: '#00ff88', connections: ['event1', 'location1', 'threat1'] },
    { id: 'threat1', x: 600, y: 300, type: 'threat', label: 'ANOMALY DETECTED', size: 22, color: '#ff0066', connections: ['system1'] },
  ];

  const sampleEdges: GraphEdge[] = [
    { from: 'entity1', to: 'event1', type: 'causes', strength: 0.8 },
    { from: 'entity1', to: 'location1', type: 'located_at', strength: 0.9 },
    { from: 'event1', to: 'system1', type: 'related', strength: 0.7 },
    { from: 'location1', to: 'system1', type: 'related', strength: 0.6 },
    { from: 'system1', to: 'threat1', type: 'causes', strength: 0.9 },
  ];

  const drawNodeGraph = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Background
    ctx.fillStyle = 'rgba(0, 10, 20, 0.9)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Draw grid
    ctx.strokeStyle = 'rgba(0, 255, 255, 0.1)';
    ctx.lineWidth = 1;
    const gridSize = 50;
    for (let x = 0; x < canvas.width; x += gridSize) {
      ctx.beginPath();
      ctx.moveTo(x, 0);
      ctx.lineTo(x, canvas.height);
      ctx.stroke();
    }
    for (let y = 0; y < canvas.height; y += gridSize) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(canvas.width, y);
      ctx.stroke();
    }

    // Draw edges
    sampleEdges.forEach(edge => {
      const fromNode = sampleNodes.find(n => n.id === edge.from);
      const toNode = sampleNodes.find(n => n.id === edge.to);

      if (fromNode && toNode) {
        ctx.strokeStyle = getEdgeColor(edge.type);
        ctx.lineWidth = edge.strength * 3;
        ctx.globalAlpha = 0.6;

        ctx.beginPath();
        ctx.moveTo(fromNode.x, fromNode.y);
        ctx.lineTo(toNode.x, toNode.y);
        ctx.stroke();

        // Draw edge label
        if (edge.label) {
          const midX = (fromNode.x + toNode.x) / 2;
          const midY = (fromNode.y + toNode.y) / 2;

          ctx.fillStyle = '#ffffff';
          ctx.font = '10px "Courier New", monospace';
          ctx.textAlign = 'center';
          ctx.globalAlpha = 0.8;
          ctx.fillText(edge.label, midX, midY);
        }

        ctx.globalAlpha = 1;
      }
    });

    // Draw nodes
    sampleNodes.forEach(node => {
      // Node circle
      ctx.fillStyle = node.color;
      ctx.strokeStyle = '#ffffff';
      ctx.lineWidth = 2;

      ctx.beginPath();
      ctx.arc(node.x, node.y, node.size, 0, 2 * Math.PI);
      ctx.fill();
      ctx.stroke();

      // Node icon (simple shape based on type)
      ctx.fillStyle = '#ffffff';
      ctx.font = 'bold 12px "Courier New", monospace';
      ctx.textAlign = 'center';
      ctx.fillText(getNodeIcon(node.type), node.x, node.y + 4);

      // Node label
      ctx.fillStyle = '#ffffff';
      ctx.font = '11px "Courier New", monospace';
      ctx.textAlign = 'center';
      ctx.fillText(node.label, node.x, node.y + node.size + 15);

      // Node type indicator
      ctx.fillStyle = 'rgba(255, 255, 255, 0.6)';
      ctx.font = '9px "Courier New", monospace';
      ctx.fillText(node.type.toUpperCase(), node.x, node.y + node.size + 28);
    });

    // Draw title
    ctx.fillStyle = '#00ffff';
    ctx.font = 'bold 16px "Courier New", monospace';
    ctx.textAlign = 'center';
    ctx.fillText('NODE GRAPH ANALYSIS', canvas.width / 2, 30);

    // Draw legend
    drawLegend(ctx, canvas.width - 200, 50);
  }, []);

  const getNodeIcon = (type: string): string => {
    switch (type) {
      case 'entity': return '‚óâ';
      case 'event': return '‚ö°';
      case 'location': return 'üìç';
      case 'system': return '‚öô';
      case 'threat': return '‚ö†';
      default: return '‚óè';
    }
  };

  const getEdgeColor = (type: string): string => {
    switch (type) {
      case 'related': return '#66ccff';
      case 'causes': return '#ff6666';
      case 'located_at': return '#66ff66';
      case 'communicates': return '#ffff66';
      default: return '#ffffff';
    }
  };

  const drawLegend = (ctx: CanvasRenderingContext2D, x: number, y: number) => {
    const legendItems = [
      { type: 'entity', label: 'ENTITY', color: '#ff3333' },
      { type: 'event', label: 'EVENT', color: '#ff6600' },
      { type: 'location', label: 'LOCATION', color: '#3366ff' },
      { type: 'system', label: 'SYSTEM', color: '#00ff88' },
      { type: 'threat', label: 'THREAT', color: '#ff0066' },
    ];

    // Legend background
    ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    ctx.fillRect(x - 10, y - 10, 180, legendItems.length * 20 + 20);

    ctx.strokeStyle = 'rgba(0, 255, 255, 0.3)';
    ctx.lineWidth = 1;
    ctx.strokeRect(x - 10, y - 10, 180, legendItems.length * 20 + 20);

    // Legend title
    ctx.fillStyle = '#00ffff';
    ctx.font = 'bold 12px "Courier New", monospace';
    ctx.textAlign = 'left';
    ctx.fillText('NODE TYPES', x, y);

    // Legend items
    legendItems.forEach((item, index) => {
      const itemY = y + 20 + (index * 20);

      // Color circle
      ctx.fillStyle = item.color;
      ctx.beginPath();
      ctx.arc(x + 10, itemY, 6, 0, 2 * Math.PI);
      ctx.fill();

      // Label
      ctx.fillStyle = '#ffffff';
      ctx.font = '10px "Courier New", monospace';
      ctx.fillText(item.label, x + 25, itemY + 4);
    });
  };

  useEffect(() => {
    drawNodeGraph();
  }, [drawNodeGraph]);

  const handleCanvasClick = (event: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Check if click is on a node
    const clickedNode = sampleNodes.find(node => {
      const distance = Math.sqrt(Math.pow(x - node.x, 2) + Math.pow(y - node.y, 2));
      return distance <= node.size;
    });

    if (clickedNode) {
      console.log('Node clicked:', clickedNode);
      // TODO: Implement node selection and context actions
    }
  };

  return (
    <div className={`${styles.nodeGraphView} ${fullscreen ? styles.fullscreen : ''} ${className || ''}`}>
      <div className={styles.header}>
        <h2 className={styles.title}>
          <span className={styles.icon}>üï∏</span>
          Intelligence Node Graph
        </h2>
        <div className={styles.controls}>
          <button className={styles.controlBtn}>FORCE</button>
          <button className={styles.controlBtn}>HIER</button>
          <button className={styles.controlBtn}>CIRC</button>
          <button className={styles.controlBtn}>GRID</button>
        </div>
      </div>

      <div className={styles.canvasContainer}>
        <canvas
          ref={canvasRef}
          className={styles.canvas}
          width={800}
          height={500}
          onClick={handleCanvasClick}
        />

        <div className={styles.overlay}>
          <div className={styles.contextInfo}>
            Context: {state.enhanced?.primaryContextId || 'GLOBAL'}
          </div>
          <div className={styles.stats}>
            <div>Nodes: {sampleNodes.length}</div>
            <div>Edges: {sampleEdges.length}</div>
          </div>
        </div>
      </div>

      <div className={styles.footer}>
        <div className={styles.algorithm}>
          LAYOUT: Force-Directed | ZOOM: 100%
        </div>
        <div className={styles.status}>
          GRAPH READY
        </div>
      </div>
    </div>
  );
};

export default NodeGraphView;

