import React, { useContext, useState, useEffect } from 'react';
import { settingsStorage } from '../utils/settingsStorage';

// Extend VisualizationMode to include sub-modes
export type VisualizationMode =
  | { mode: 'CyberCommand'; subMode: 'IntelReports' | 'Timelines' | 'CrisisZones' }
  | { mode: 'GeoPolitical'; subMode: 'NationalTerritories' | 'DiplomaticEvents' | 'ResourceZones' }
  | { mode: 'EcoNatural'; subMode: 'SpaceWeather' | 'EcologicalDisasters' | 'EarthWeather' };

// Interface for tracking last selected submode for each primary mode
interface LastSelectedSubmodes {
  CyberCommand: 'IntelReports' | 'Timelines' | 'CrisisZones';
  GeoPolitical: 'NationalTerritories' | 'DiplomaticEvents' | 'ResourceZones';
  EcoNatural: 'SpaceWeather' | 'EcologicalDisasters' | 'EarthWeather';
}

// Default visualization mode and submodes
const DEFAULT_MODE: VisualizationMode = { mode: 'CyberCommand', subMode: 'IntelReports' };
const DEFAULT_SUBMODES: LastSelectedSubmodes = {
  CyberCommand: 'IntelReports',
  GeoPolitical: 'NationalTerritories',
  EcoNatural: 'SpaceWeather'
};

// Storage keys for persistence
const VISUALIZATION_MODE_STORAGE_KEY = 'visualization-mode';
const LAST_SUBMODES_STORAGE_KEY = 'last-selected-submodes';

// Create a context for VisualizationMode
interface VisualizationModeContextProps {
  visualizationMode: VisualizationMode;
  setVisualizationMode: (mode: VisualizationMode) => void;
  setPrimaryMode: (mode: 'CyberCommand' | 'GeoPolitical' | 'EcoNatural') => void;
  resetVisualizationMode: () => void;
}

// Ensure React is properly loaded before creating context
const VisualizationModeContext = React.createContext<VisualizationModeContextProps | undefined>(undefined);

// Provider component
export const VisualizationModeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // Initialize with persisted values or defaults
  const [visualizationMode, setVisualizationModeState] = useState<VisualizationMode>(() => {
    return settingsStorage.loadSettings(VISUALIZATION_MODE_STORAGE_KEY, DEFAULT_MODE);
  });

  const [lastSelectedSubmodes, setLastSelectedSubmodesState] = useState<LastSelectedSubmodes>(() => {
    return settingsStorage.loadSettings(LAST_SUBMODES_STORAGE_KEY, DEFAULT_SUBMODES);
  });

  // Enhanced setter that also persists to storage
  const setVisualizationMode = (mode: VisualizationMode) => {
    setVisualizationModeState(mode);
    settingsStorage.saveSettings(VISUALIZATION_MODE_STORAGE_KEY, mode);

    // Update the last selected submode for this primary mode
    setLastSelectedSubmodesState(prev => {
      const updated = { ...prev, [mode.mode]: mode.subMode };
      settingsStorage.saveSettings(LAST_SUBMODES_STORAGE_KEY, updated);
      return updated;
    });
  };

  // New function to set primary mode while preserving last selected submode
  const setPrimaryMode = (mode: 'CyberCommand' | 'GeoPolitical' | 'EcoNatural') => {
    const lastSubMode = lastSelectedSubmodes[mode];
    const newVisualizationMode = { mode, subMode: lastSubMode } as VisualizationMode;
    setVisualizationMode(newVisualizationMode);
  };

  const resetVisualizationMode = () => {
    setVisualizationMode(DEFAULT_MODE);
    setLastSelectedSubmodesState(DEFAULT_SUBMODES);
    settingsStorage.saveSettings(LAST_SUBMODES_STORAGE_KEY, DEFAULT_SUBMODES);
  };

  useEffect(() => {
    // Only log in dev mode to reduce console noise
    if (import.meta.env.DEV) {
      console.log(`üìä VisualizationMode changed to: ${visualizationMode.mode}, SubMode: ${visualizationMode.subMode}`);
    }
  }, [visualizationMode]);

  return (
    <VisualizationModeContext.Provider value={{ 
      visualizationMode, 
      setVisualizationMode, 
      setPrimaryMode,
      resetVisualizationMode 
    }}>
      {children}
    </VisualizationModeContext.Provider>
  );
};

// Hook to use the VisualizationMode context
export const useVisualizationMode = () => {
  const context = useContext(VisualizationModeContext);
  if (!context) {
    throw new Error('useVisualizationMode must be used within a VisualizationModeProvider');
  }
  return context;
};

import React, { createContext, useContext, useState, useEffect } from "react";
import { initializeWASM, fetchFromMiniServer as fetchFromWASM } from "../utils/wasm";

interface WASMContextType {
  wasmReady: boolean;
  fetchFromMiniServer: (url: string) => Promise<any>;
}

const WASMContext = createContext<WASMContextType | undefined>(undefined);

interface WASMProviderProps {
  children: React.ReactNode;
}

let wasmInitializationPromise: Promise<void> | null = null;

const WASMProvider: React.FC<WASMProviderProps> = ({ children }) => {
  const [wasmReady, setWasmReady] = useState(false);

  useEffect(() => {
    if (!wasmInitializationPromise) {
      wasmInitializationPromise = initializeWASM().then(() => {
        setWasmReady(true);
      });
    } else {
      wasmInitializationPromise.then(() => {
        setWasmReady(true);
      });
    }
  }, []);

  const fetchFromMiniServer = async (url: string) => {
    return await fetchFromWASM(url);
  };

  return (
    <WASMContext.Provider value={{ wasmReady, fetchFromMiniServer }}>
      {children}
    </WASMContext.Provider>
  );
};

const useWASM = () => {
  const context = useContext(WASMContext);
  if (!context) {
    throw new Error("useWASM must be used within a WASMProvider");
  }
  return context;
};

export { WASMProvider, useWASM, WASMContext };

import { useState, useEffect } from 'react';

export const useFetchData = <T>(url: string, options = {}) => {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        const response = await fetch(url, options);
        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err as Error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [url]);

  return { data, loading, error };
};

/**
 * TDD Feature 10: Enhanced Error Handling Hook
 * 
 * Implements comprehensive error handling with detailed context, automatic recovery
 * strategies, and user-friendly error reporting for authentication flows.
 */

import { useState, useEffect, useCallback } from 'react';

// Types for enhanced error handling
export interface AuthError {
  id: string;
  timestamp: number;
  type: 'network' | 'authentication' | 'authorization' | 'validation' | 'biometric' | 'session' | 'configuration' | 'unknown';
  severity: 'low' | 'medium' | 'high' | 'critical';
  code: string;
  message: string;
  context: ErrorContext;
  stack?: string;
  recoverable: boolean;
  retryable: boolean;
  userMessage: string;
  suggestedActions: string[];
}

export interface ErrorContext {
  userId?: string;
  deviceId: string;
  sessionId?: string;
  url?: string;
  userAgent?: string;
  timestamp: number;
  operation: string;
  parameters?: Record<string, unknown>;
  previousErrors?: string[];
  systemState?: Record<string, unknown>;
}

export interface RecoveryStrategy {
  id: string;
  name: string;
  description: string;
  errorTypes: AuthError['type'][];
  errorCodes: string[];
  automatic: boolean;
  priority: number;
  maxRetries: number;
  execute: (error: AuthError) => Promise<RecoveryResult>;
}

export interface RecoveryResult {
  success: boolean;
  newError?: AuthError;
  message?: string;
  retryAfter?: number;
  actionRequired?: 'user_input' | 'manual_intervention' | 'system_restart' | 'none';
}

export interface ErrorHandlingConfig {
  enableAutoRecovery: boolean;
  maxAutoRetries: number;
  retryDelayMs: number;
  enableUserNotifications: boolean;
  logLevel: 'minimal' | 'standard' | 'verbose' | 'debug';
  enableRecoveryStrategies: boolean;
  notificationDelay: number;
}

export interface UseEnhancedErrorHandlingReturn {
  // Error Reporting
  reportError: (error: unknown, context: Partial<ErrorContext>) => Promise<AuthError>;
  getRecentErrors: (limit?: number) => Promise<AuthError[]>;
  clearErrorHistory: () => Promise<boolean>;

  // Error Recovery
  attemptRecovery: (errorId: string) => Promise<RecoveryResult>;
  registerRecoveryStrategy: (strategy: RecoveryStrategy) => Promise<boolean>;
  getAvailableRecoveries: (error: AuthError) => Promise<RecoveryStrategy[]>;

  // Error Analysis
  analyzeError: (error: unknown) => AuthError;
  getErrorStats: () => Promise<ErrorStats>;
  getErrorTrends: () => Promise<ErrorTrend[]>;

  // Configuration
  updateErrorConfig: (config: Partial<ErrorHandlingConfig>) => Promise<boolean>;
  getErrorConfig: () => ErrorHandlingConfig;

  // State
  recentErrors: AuthError[];
  activeRecoveries: string[];
  errorHandlingEnabled: boolean;
  autoRecoveryEnabled: boolean;
}

export interface ErrorStats {
  totalErrors: number;
  errorsByType: Record<string, number>;
  errorsBySeverity: Record<string, number>;
  recoverySuccessRate: number;
  mostCommonErrors: Array<{ code: string; count: number; message: string }>;
  errorFrequency: number; // errors per hour
}

export interface ErrorTrend {
  timestamp: number;
  errorCount: number;
  errorType: string;
  severity: string;
}

/**
 * Default error handling configuration
 */
const DEFAULT_ERROR_CONFIG: ErrorHandlingConfig = {
  enableAutoRecovery: true,
  maxAutoRetries: 3,
  retryDelayMs: 1000,
  enableUserNotifications: true,
  logLevel: 'standard',
  enableRecoveryStrategies: true,
  notificationDelay: 500
};

/**
 * Built-in recovery strategies
 */
const BUILTIN_RECOVERY_STRATEGIES: RecoveryStrategy[] = [
  {
    id: 'network_retry',
    name: 'Network Retry',
    description: 'Retry network operations with exponential backoff',
    errorTypes: ['network'],
    errorCodes: ['NETWORK_ERROR', 'TIMEOUT', 'CONNECTION_FAILED'],
    automatic: true,
    priority: 1,
    maxRetries: 3,
    execute: async (_error: AuthError): Promise<RecoveryResult> => {
      // Simulate network retry logic
      await new Promise(resolve => setTimeout(resolve, 1000));
      return {
        success: Math.random() > 0.3, // 70% success rate
        message: 'Network retry attempted',
        retryAfter: 2000
      };
    }
  },
  {
    id: 'session_refresh',
    name: 'Session Refresh',
    description: 'Refresh authentication session when expired',
    errorTypes: ['session', 'authentication'],
    errorCodes: ['SESSION_EXPIRED', 'TOKEN_INVALID', 'AUTH_REQUIRED'],
    automatic: true,
    priority: 2,
    maxRetries: 2,
    execute: async (_error: AuthError): Promise<RecoveryResult> => {
      // Simulate session refresh logic
      await new Promise(resolve => setTimeout(resolve, 500));
      return {
        success: Math.random() > 0.2, // 80% success rate
        message: 'Session refresh attempted',
        actionRequired: 'user_input'
      };
    }
  },
  {
    id: 'biometric_fallback',
    name: 'Biometric Fallback',
    description: 'Fall back to alternative authentication when biometric fails',
    errorTypes: ['biometric'],
    errorCodes: ['BIOMETRIC_FAILED', 'BIOMETRIC_UNAVAILABLE', 'WEBAUTHN_ERROR'],
    automatic: true,
    priority: 3,
    maxRetries: 1,
    execute: async (_error: AuthError): Promise<RecoveryResult> => {
      // Simulate biometric fallback
      await new Promise(resolve => setTimeout(resolve, 300));
      return {
        success: true,
        message: 'Falling back to password authentication',
        actionRequired: 'user_input'
      };
    }
  }
];

/**
 * Generate unique error ID
 */
const generateErrorId = (): string => {
  return `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
};

/**
 * Get device information for error context
 */
const getErrorDeviceInfo = (): { deviceId: string; userAgent: string } => {
  let deviceId = localStorage.getItem('starcom_device_id');
  if (!deviceId) {
    deviceId = `dev_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    localStorage.setItem('starcom_device_id', deviceId);
  }

  return {
    deviceId,
    userAgent: navigator.userAgent
  };
};

/**
 * Hook for enhanced error handling
 */
export const useEnhancedErrorHandling = (): UseEnhancedErrorHandlingReturn => {
  const [errorConfig, setErrorConfig] = useState<ErrorHandlingConfig>(DEFAULT_ERROR_CONFIG);
  const [recentErrors, setRecentErrors] = useState<AuthError[]>([]);
  const [activeRecoveries, setActiveRecoveries] = useState<string[]>([]);
  const [recoveryStrategies, setRecoveryStrategies] = useState<RecoveryStrategy[]>(BUILTIN_RECOVERY_STRATEGIES);

  const errorHandlingEnabled = true; // Always enabled
  const autoRecoveryEnabled = errorConfig.enableAutoRecovery;

  // Load stored errors
  const getStoredErrors = useCallback((): AuthError[] => {
    try {
      const stored = localStorage.getItem('starcom_error_history');
      return stored ? JSON.parse(stored) : [];
    } catch (error) {
      console.error('Failed to load error history:', error);
      return [];
    }
  }, []);

  // Store errors
  const storeErrors = useCallback((errors: AuthError[]): void => {
    try {
      // Keep only last 100 errors
      const limitedErrors = errors.slice(-100);
      localStorage.setItem('starcom_error_history', JSON.stringify(limitedErrors));
      setRecentErrors(limitedErrors);
    } catch (error) {
      console.error('Failed to store error history:', error);
    }
  }, []);

  // Analyze and categorize error
  const analyzeError = useCallback((error: unknown): AuthError => {
    const deviceInfo = getErrorDeviceInfo();
    let errorType: AuthError['type'] = 'unknown';
    let severity: AuthError['severity'] = 'medium';
    let code = 'UNKNOWN_ERROR';
    let message = 'An unknown error occurred';
    let userMessage = 'Something went wrong. Please try again.';
    let suggestedActions: string[] = ['Try again', 'Check your connection'];
    let recoverable = true;
    let retryable = true;

    if (error instanceof Error) {
      message = error.message;

      // Categorize error based on message/type
      if (error.message.includes('network') || error.message.includes('fetch') || error.message.includes('timeout')) {
        errorType = 'network';
        code = 'NETWORK_ERROR';
        userMessage = 'Network connection problem. Please check your internet connection.';
        suggestedActions = ['Check internet connection', 'Try again', 'Switch networks'];
        severity = 'medium';
      } else if (error.message.includes('auth') || error.message.includes('login') || error.message.includes('token')) {
        errorType = 'authentication';
        code = 'AUTH_ERROR';
        userMessage = 'Authentication failed. Please try logging in again.';
        suggestedActions = ['Log in again', 'Clear browser cache', 'Contact support'];
        severity = 'high';
      } else if (error.message.includes('permission') || error.message.includes('forbidden') || error.message.includes('unauthorized')) {
        errorType = 'authorization';
        code = 'PERMISSION_DENIED';
        userMessage = 'You don\'t have permission to perform this action.';
        suggestedActions = ['Contact administrator', 'Check your role permissions'];
        severity = 'medium';
        retryable = false;
      } else if (error.message.includes('biometric') || error.message.includes('webauthn')) {
        errorType = 'biometric';
        code = 'BIOMETRIC_ERROR';
        userMessage = 'Biometric authentication failed. Try using an alternative method.';
        suggestedActions = ['Use password', 'Try biometric again', 'Check device settings'];
        severity = 'low';
      } else if (error.message.includes('session') || error.message.includes('expired')) {
        errorType = 'session';
        code = 'SESSION_ERROR';
        userMessage = 'Your session has expired. Please log in again.';
        suggestedActions = ['Log in again', 'Refresh page'];
        severity = 'medium';
      } else if (error.message.includes('validation') || error.message.includes('invalid')) {
        errorType = 'validation';
        code = 'VALIDATION_ERROR';
        userMessage = 'Please check your input and try again.';
        suggestedActions = ['Check input format', 'Try again'];
        severity = 'low';
      } else if (error.message.includes('config') || error.message.includes('configuration')) {
        errorType = 'configuration';
        code = 'CONFIG_ERROR';
        userMessage = 'System configuration error. Please contact support.';
        suggestedActions = ['Contact support', 'Try again later'];
        severity = 'critical';
        recoverable = false;
      }
    }

    return {
      id: generateErrorId(),
      timestamp: Date.now(),
      type: errorType,
      severity,
      code,
      message,
      context: {
        ...deviceInfo,
        timestamp: Date.now(),
        operation: 'unknown',
        url: window.location.href
      },
      stack: error instanceof Error ? error.stack : undefined,
      recoverable,
      retryable,
      userMessage,
      suggestedActions
    };
  }, []);

  // Report error with context
  const reportError = useCallback(async (error: unknown, context: Partial<ErrorContext> = {}): Promise<AuthError> => {
    try {
      const analyzedError = analyzeError(error);

      // Enhance context
      analyzedError.context = {
        ...analyzedError.context,
        ...context
      };

      // Store error
      const errors = getStoredErrors();
      errors.push(analyzedError);
      storeErrors(errors);

      // Attempt automatic recovery if enabled
      if (autoRecoveryEnabled && analyzedError.recoverable) {
        setActiveRecoveries(prev => [...prev, analyzedError.id]);

        // Find suitable recovery strategy
        const strategy = recoveryStrategies.find(s => 
          s.automatic && 
          s.errorTypes.includes(analyzedError.type) &&
          s.errorCodes.includes(analyzedError.code)
        );

        if (strategy) {
          try {
            const recoveryResult = await strategy.execute(analyzedError);
            if (recoveryResult.success) {
              console.log(`Auto-recovery successful for error ${analyzedError.id}`);
            }
          } catch (recoveryError) {
            console.error('Auto-recovery failed:', recoveryError);
          } finally {
            setActiveRecoveries(prev => prev.filter(id => id !== analyzedError.id));
          }
        }
      }

      return analyzedError;
    } catch (reportingError) {
      console.error('Failed to report error:', reportingError);

      // Return a basic error if reporting fails
      return {
        id: generateErrorId(),
        timestamp: Date.now(),
        type: 'unknown',
        severity: 'critical',
        code: 'ERROR_REPORTING_FAILED',
        message: 'Failed to report error',
        context: {
          deviceId: 'unknown',
          timestamp: Date.now(),
          operation: 'error_reporting'
        },
        recoverable: false,
        retryable: false,
        userMessage: 'An error occurred while reporting another error.',
        suggestedActions: ['Refresh page', 'Contact support']
      };
    }
  }, [analyzeError, getStoredErrors, storeErrors, autoRecoveryEnabled, recoveryStrategies]);

  // Get recent errors
  const getRecentErrors = useCallback(async (limit = 10): Promise<AuthError[]> => {
    try {
      const errors = getStoredErrors();
      return errors.slice(-limit).reverse(); // Most recent first
    } catch (error) {
      console.error('Failed to get recent errors:', error);
      return [];
    }
  }, [getStoredErrors]);

  // Clear error history
  const clearErrorHistory = useCallback(async (): Promise<boolean> => {
    try {
      localStorage.removeItem('starcom_error_history');
      setRecentErrors([]);
      return true;
    } catch (error) {
      console.error('Failed to clear error history:', error);
      return false;
    }
  }, []);

  // Attempt manual recovery
  const attemptRecovery = useCallback(async (errorId: string): Promise<RecoveryResult> => {
    try {
      const errors = getStoredErrors();
      const targetError = errors.find(e => e.id === errorId);

      if (!targetError) {
        return {
          success: false,
          message: 'Error not found'
        };
      }

      if (!targetError.recoverable) {
        return {
          success: false,
          message: 'Error is not recoverable'
        };
      }

      // Find suitable recovery strategy
      const strategy = recoveryStrategies.find(s => 
        s.errorTypes.includes(targetError.type) &&
        s.errorCodes.includes(targetError.code)
      );

      if (!strategy) {
        return {
          success: false,
          message: 'No recovery strategy available'
        };
      }

      setActiveRecoveries(prev => [...prev, errorId]);

      try {
        const result = await strategy.execute(targetError);
        return result;
      } finally {
        setActiveRecoveries(prev => prev.filter(id => id !== errorId));
      }
    } catch (error) {
      console.error('Recovery attempt failed:', error);
      return {
        success: false,
        message: 'Recovery attempt failed'
      };
    }
  }, [getStoredErrors, recoveryStrategies]);

  // Register custom recovery strategy
  const registerRecoveryStrategy = useCallback(async (strategy: RecoveryStrategy): Promise<boolean> => {
    try {
      setRecoveryStrategies(prev => {
        // Remove existing strategy with same ID
        const filtered = prev.filter(s => s.id !== strategy.id);
        // Add new strategy sorted by priority
        const updated = [...filtered, strategy].sort((a, b) => a.priority - b.priority);
        return updated;
      });
      return true;
    } catch (error) {
      console.error('Failed to register recovery strategy:', error);
      return false;
    }
  }, []);

  // Get available recovery strategies for an error
  const getAvailableRecoveries = useCallback(async (error: AuthError): Promise<RecoveryStrategy[]> => {
    return recoveryStrategies.filter(strategy => 
      strategy.errorTypes.includes(error.type) &&
      (strategy.errorCodes.length === 0 || strategy.errorCodes.includes(error.code))
    );
  }, [recoveryStrategies]);

  // Get error statistics
  const getErrorStats = useCallback(async (): Promise<ErrorStats> => {
    try {
      const errors = getStoredErrors();

      const errorsByType = errors.reduce((acc, error) => {
        acc[error.type] = (acc[error.type] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

      const errorsBySeverity = errors.reduce((acc, error) => {
        acc[error.severity] = (acc[error.severity] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

      // Calculate recovery success rate (simplified)
      const recoverableErrors = errors.filter(e => e.recoverable).length;
      const recoverySuccessRate = recoverableErrors > 0 ? 70 : 0; // Simulated 70% success rate

      const errorCounts = errors.reduce((acc, error) => {
        const key = error.code;
        if (!acc[key]) {
          acc[key] = { count: 0, message: error.message };
        }
        acc[key].count++;
        return acc;
      }, {} as Record<string, { count: number; message: string }>);

      const mostCommonErrors = Object.entries(errorCounts)
        .map(([code, data]) => ({ code, count: data.count, message: data.message }))
        .sort((a, b) => b.count - a.count)
        .slice(0, 5);

      const now = Date.now();
      const oneHourAgo = now - (60 * 60 * 1000);
      const recentErrorCount = errors.filter(e => e.timestamp > oneHourAgo).length;
      const errorFrequency = recentErrorCount;

      return {
        totalErrors: errors.length,
        errorsByType,
        errorsBySeverity,
        recoverySuccessRate,
        mostCommonErrors,
        errorFrequency
      };
    } catch (error) {
      console.error('Failed to get error stats:', error);
      return {
        totalErrors: 0,
        errorsByType: {},
        errorsBySeverity: {},
        recoverySuccessRate: 0,
        mostCommonErrors: [],
        errorFrequency: 0
      };
    }
  }, [getStoredErrors]);

  // Get error trends
  const getErrorTrends = useCallback(async (): Promise<ErrorTrend[]> => {
    try {
      const errors = getStoredErrors();
      const now = Date.now();
      const oneDayAgo = now - (24 * 60 * 60 * 1000);

      // Group errors by hour for the last 24 hours
      const trends: ErrorTrend[] = [];
      for (let i = 0; i < 24; i++) {
        const hourStart = oneDayAgo + (i * 60 * 60 * 1000);
        const hourEnd = hourStart + (60 * 60 * 1000);

        const hourErrors = errors.filter(e => e.timestamp >= hourStart && e.timestamp < hourEnd);

        if (hourErrors.length > 0) {
          // Group by type and severity
          const typeGroups = hourErrors.reduce((acc, error) => {
            const key = `${error.type}_${error.severity}`;
            if (!acc[key]) {
              acc[key] = { errorType: error.type, severity: error.severity, count: 0 };
            }
            acc[key].count++;
            return acc;
          }, {} as Record<string, { errorType: string; severity: string; count: number }>);

          Object.values(typeGroups).forEach(group => {
            trends.push({
              timestamp: hourStart,
              errorCount: group.count,
              errorType: group.errorType,
              severity: group.severity
            });
          });
        }
      }

      return trends;
    } catch (error) {
      console.error('Failed to get error trends:', error);
      return [];
    }
  }, [getStoredErrors]);

  // Update error configuration
  const updateErrorConfig = useCallback(async (configUpdate: Partial<ErrorHandlingConfig>): Promise<boolean> => {
    try {
      const newConfig = { ...errorConfig, ...configUpdate };
      setErrorConfig(newConfig);
      localStorage.setItem('starcom_error_config', JSON.stringify(newConfig));
      return true;
    } catch (error) {
      console.error('Failed to update error config:', error);
      return false;
    }
  }, [errorConfig]);

  // Get error configuration
  const getErrorConfig = useCallback((): ErrorHandlingConfig => {
    return errorConfig;
  }, [errorConfig]);

  // Load persisted data on mount
  useEffect(() => {
    // Load configuration
    const savedConfig = localStorage.getItem('starcom_error_config');
    if (savedConfig) {
      try {
        const config = JSON.parse(savedConfig);
        setErrorConfig({ ...DEFAULT_ERROR_CONFIG, ...config });
      } catch (error) {
        console.error('Failed to load error config:', error);
      }
    }

    // Load recent errors
    const errors = getStoredErrors();
    setRecentErrors(errors.slice(-10).reverse());
  }, [getStoredErrors]);

  return {
    // Error Reporting
    reportError,
    getRecentErrors,
    clearErrorHistory,

    // Error Recovery
    attemptRecovery,
    registerRecoveryStrategy,
    getAvailableRecoveries,

    // Error Analysis
    analyzeError,
    getErrorStats,
    getErrorTrends,

    // Configuration
    updateErrorConfig,
    getErrorConfig,

    // State
    recentErrors,
    activeRecoveries,
    errorHandlingEnabled,
    autoRecoveryEnabled
  };
};

export default useEnhancedErrorHandling;

/**
 * Progressive Authentication System
 * Provides guest mode with limited access and seamless upgrade to full authentication
 * Implements TDD Feature 4: Progressive Authentication
 */

import { useState, useCallback, useEffect } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import { encryptedSessionStorage } from '../utils/encryptedStorage';

export interface GuestSession {
  id: string;
  created: number;
  lastActivity: number;
  permissions: string[];
  data: Record<string, unknown>;
  canUpgrade: boolean;
}

export interface ProgressiveAuthState {
  mode: 'guest' | 'authenticated' | 'upgrading';
  guestSession: GuestSession | null;
  isUpgrading: boolean;
}

interface UseProgressiveAuthReturn {
  authState: ProgressiveAuthState;
  createGuestSession: () => GuestSession;
  upgradeToFullAuth: () => Promise<boolean>;
  getGuestPermissions: () => string[];
  canUpgradeFromGuest: boolean;
  preserveGuestData: (data: Record<string, unknown>) => void;
  clearGuestSession: () => void;
  isGuestMode: boolean;
  isAuthenticated: boolean;
}

const GUEST_SESSION_KEY = 'progressive-auth-guest';
const GUEST_SESSION_TTL = 24 * 60 * 60 * 1000; // 24 hours
const GUEST_PERMISSIONS = ['read', 'browse', 'search', 'view-public'];
const RESTRICTED_PERMISSIONS = ['write', 'trade', 'transfer', 'admin'];

export const useProgressiveAuth = (): UseProgressiveAuthReturn => {
  const wallet = useWallet();
  const [authState, setAuthState] = useState<ProgressiveAuthState>({
    mode: 'guest',
    guestSession: null,
    isUpgrading: false
  });

  // Load existing guest session on mount
  useEffect(() => {
    loadGuestSession();
  }, []);

  // Monitor wallet connection for auto-upgrade opportunities
  useEffect(() => {
    if (wallet.connected && authState.mode === 'guest' && authState.guestSession?.canUpgrade) {
      // Auto-suggest upgrade when wallet connects
      console.log('[ProgressiveAuth] Wallet connected - upgrade available');
    }
  }, [wallet.connected, authState.mode, authState.guestSession?.canUpgrade]);

  const generateSessionId = (): string => {
    return `guest_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  };

  const createGuestSession = useCallback((): GuestSession => {
    const guestSession: GuestSession = {
      id: generateSessionId(),
      created: Date.now(),
      lastActivity: Date.now(),
      permissions: [...GUEST_PERMISSIONS],
      data: {},
      canUpgrade: true
    };

    // Store guest session securely
    try {
      encryptedSessionStorage.setItem(GUEST_SESSION_KEY, guestSession);
      setAuthState(prev => ({
        ...prev,
        mode: 'guest',
        guestSession
      }));

      console.log('[ProgressiveAuth] Guest session created:', guestSession.id);
    } catch (error) {
      console.error('[ProgressiveAuth] Failed to create guest session:', error);
    }

    return guestSession;
  }, []);

  const loadGuestSession = useCallback((): void => {
    try {
      const stored = encryptedSessionStorage.getItem<GuestSession>(GUEST_SESSION_KEY);

      if (stored) {
        const now = Date.now();
        const isExpired = (now - stored.created) > GUEST_SESSION_TTL;

        if (isExpired) {
          console.log('[ProgressiveAuth] Guest session expired, clearing');
          clearGuestSession();
          return;
        }

        // Update last activity
        stored.lastActivity = now;
        encryptedSessionStorage.setItem(GUEST_SESSION_KEY, stored);

        setAuthState(prev => ({
          ...prev,
          mode: 'guest',
          guestSession: stored
        }));

        console.log('[ProgressiveAuth] Guest session restored:', stored.id);
      }
    } catch (error) {
      console.error('[ProgressiveAuth] Failed to load guest session:', error);
      clearGuestSession();
    }
  }, []);

  const upgradeToFullAuth = useCallback(async (): Promise<boolean> => {
    if (!authState.guestSession) {
      console.warn('[ProgressiveAuth] No guest session to upgrade');
      return false;
    }

    if (!wallet.connected || !wallet.publicKey) {
      console.warn('[ProgressiveAuth] Wallet not connected for upgrade');
      return false;
    }

    setAuthState(prev => ({
      ...prev,
      mode: 'upgrading',
      isUpgrading: true
    }));

    try {
      // Preserve guest session data during upgrade
      const guestData = { ...authState.guestSession.data };

      // Simulate authentication process (integrate with existing auth system)
      await new Promise(resolve => setTimeout(resolve, 1000));

      // On successful upgrade, preserve data and transition to authenticated state
      setAuthState({
        mode: 'authenticated',
        guestSession: null,
        isUpgrading: false
      });

      // Store preserved data in authenticated session context
      if (Object.keys(guestData).length > 0) {
        // This would integrate with the main auth context to preserve data
        console.log('[ProgressiveAuth] Guest data preserved during upgrade:', guestData);
      }

      // Clear guest session after successful upgrade
      encryptedSessionStorage.removeItem(GUEST_SESSION_KEY);

      console.log('[ProgressiveAuth] Successfully upgraded to full authentication');
      return true;

    } catch (error) {
      console.error('[ProgressiveAuth] Upgrade failed:', error);

      // Rollback to guest mode on failure
      setAuthState(prev => ({
        ...prev,
        mode: 'guest',
        isUpgrading: false
      }));

      return false;
    }
  }, [authState.guestSession, wallet.connected, wallet.publicKey]);

  const getGuestPermissions = useCallback((): string[] => {
    return [...GUEST_PERMISSIONS];
  }, []);

  const preserveGuestData = useCallback((data: Record<string, unknown>): void => {
    if (!authState.guestSession) {
      console.warn('[ProgressiveAuth] No guest session to preserve data');
      return;
    }

    const updatedSession = {
      ...authState.guestSession,
      data: { ...authState.guestSession.data, ...data },
      lastActivity: Date.now()
    };

    try {
      encryptedSessionStorage.setItem(GUEST_SESSION_KEY, updatedSession);
      setAuthState(prev => ({
        ...prev,
        guestSession: updatedSession
      }));

      console.log('[ProgressiveAuth] Guest data preserved');
    } catch (error) {
      console.error('[ProgressiveAuth] Failed to preserve guest data:', error);
    }
  }, [authState.guestSession]);

  const clearGuestSession = useCallback((): void => {
    try {
      encryptedSessionStorage.removeItem(GUEST_SESSION_KEY);
      setAuthState({
        mode: 'guest',
        guestSession: null,
        isUpgrading: false
      });

      console.log('[ProgressiveAuth] Guest session cleared');
    } catch (error) {
      console.error('[ProgressiveAuth] Failed to clear guest session:', error);
    }
  }, []);

  // Computed properties
  const canUpgradeFromGuest = authState.guestSession?.canUpgrade === true && wallet.connected;
  const isGuestMode = authState.mode === 'guest' && authState.guestSession !== null;
  const isAuthenticated = authState.mode === 'authenticated';

  return {
    authState,
    createGuestSession,
    upgradeToFullAuth,
    getGuestPermissions,
    canUpgradeFromGuest,
    preserveGuestData,
    clearGuestSession,
    isGuestMode,
    isAuthenticated
  };
};

/**
 * Permission checker for progressive authentication
 */
export const hasPermission = (permission: string, authState: ProgressiveAuthState): boolean => {
  if (authState.mode === 'authenticated') {
    return true; // Full auth has all permissions
  }

  if (authState.mode === 'guest' && authState.guestSession) {
    return authState.guestSession.permissions.includes(permission);
  }

  return false;
};

/**
 * Get restricted features that require full authentication
 */
export const getRestrictedFeatures = (): string[] => {
  return [...RESTRICTED_PERMISSIONS];
};

/**
 * Progressive auth context integration helper
 */
export const createProgressiveAuthContext = () => {
  // This would integrate with the main AuthContext
  // For now, we return the hook interface
  return {
    useProgressiveAuth,
    hasPermission,
    getRestrictedFeatures
  };
};

